sections.css:
@import "a";
@import "dl";
@import "fieldset";
@import "img";
@import "input";
@import "label";
@import "legend";
@import "p";

a {
  color: $color-type;

  &:focus,
  &:hover {
    color: $color-type;
  }
}

/**
 * 1. Default bottom-margin of 1.5 times the font-size
 * 2. Using single colon so it works in IE8 as well
 * 3. No bottom-margin for all the last-childs so parents can have bottom padding
 */

dl {
  @include clearfix;
  margin-bottom: 1.5em; /* 1 */

  &:last-child { /* 2 */
    margin-bottom: 0; /* 3 */
  }
}

dt {
  @include reset;
  font-weight: $bold;
  float: left;
  clear: left;
  margin-right: $spacing-tiny;
}

dd {
  @include reset;
  display: block;
  margin-bottom: $spacing-tiny;

  &:last-child {
    margin-bottom: 0; /* 3 */
  }
}

/**
 * 1. Chrome and Firefox set a `min-width: min-content;` on fieldsets,
 *    so we reset that to ensure it behaves more like a standard block element.
 *    See https://github.com/twbs/bootstrap/issues/12359.
 * 2. Default bottom-margin of 1.5 times the font-size
 * 3. Using single colon so it works in IE8 as well
 * 4. No bottom-margin for all the last-childs so parents can have bottom padding
 */

fieldset {
  padding: 0;
  margin: 0;
  border: 0;
  min-width: 0; /* 1 */
  margin-bottom: 1.5em; /* 2 */

  &:last-child { /* 3 */
    margin-bottom: 0; /* 4 */
  }
}

/**
 * 1. Responsive images
 * 2. Eliminating line-height bug when used alone in container
 */

img {
  max-width: 100%; /* 1 */
  height: auto;

  &:only-child {
    vertical-align: middle; /* 2 */
  }
}

/**
 * 1. Force IE8 to wrap long content
 */

label {
  display: inline-block;
  vertical-align: middle;
  max-width: 100%; /* 1 */
  margin-bottom: 5px;
  font-weight: bold;

  &:last-child {
    margin-bottom: 0;
  }
}

legend {
  width: 100%;
  padding: 0;
  color: $legend-color;
  border: 0;
  display: block;
  margin-top: 0;
  margin-bottom: $spacing;
  border: none;
  border-bottom: 1px solid darker($component-background);
  padding-top: $spacing;
  padding-bottom: 4px;
  font-size: $font-size--large;
  line-height: $line-height--large;
  font-weight: $regular;

  /* No margin top for the first legend */
  &:first-child {
    margin-top: 0;
  }

  /* No margin bottom for the last legend */
  &:last-child {
    margin-bottom: 0;
  }
}

/**
 * 1. Remove default top-margin
 * 2. Default bottom-margin of 1.5 times the font-size
 * 3. Using single colon so it works in IE8 as well
 * 4. No bottom-margin for all the last-childs so parents can have bottom padding
 */

p {
  margin-top: 0; /* 1 */
  margin-bottom: 1.5em; /* 2 */

  &:last-child { /* 3 */
    margin-bottom: 0; /* 4 */
  }
}

@import "alerts";
@import "breadcrumb";
@import "buttons";
@import "card";
@import "empty-state";
@import "form-block";
@import "forms";
@import "gallery";
@import "heading";
@import "icons";
@import "list";
@import "masthead";
@import "modal";
@import "navigation";
@import "optionlist";
@import "scopebar";
@import "sidebar-item";
@import "table";
@import "tabs";
@import "title-bar";
@import "toolbar";
@import "tooltip";
@import "tree";
@import "wells";

.k-alert {
  padding: $alert-padding;
  margin-bottom: $line-height-computed;

  &:last-child {
    margin-bottom: 0;
  }
}

/* Alternate styles */

.k-alert--success {
  @include alert-variant($alert-success-bg, $alert-success-text);
}

.k-alert--info {
  @include alert-variant($alert-info-bg, $alert-info-text);
}

.k-alert--warning {
  @include alert-variant($alert-warning-bg, $alert-warning-text);
}

.k-alert--danger {
  @include alert-variant($alert-danger-bg, $alert-danger-text);
}

/* Parent element
   ========================================================================== */

/**
 * 1. Scroll when items don't fit parent anymore
 */

.k-breadcrumb {
  @include border(bottom, $breadcrumb-border);
  overflow: auto; /* 1 */
  background: $breadcrumb-background;
  color: $breadcrumb-color;
  min-width: 100%;

  @include resizing {
    overflow: hidden;
  }
}


/* When flexbox is in use
   ========================================================================== */

@include flexbox {
  .k-breadcrumb {
    flex: 0 0 auto;
  }
}


/* Breadcrumb contents
   ========================================================================== */

.k-breadcrumb {

  /**
   * Links
   *
   * 1. Full background hover
   */

  a {
    color: $breadcrumb-color;
    cursor: pointer;

    &:focus,
    &:hover {
      background: $breadcrumb-background-hover; /* 1 */
    }
  }


  /**
   * List
   *
   * 1. Reset unordered list styling
   */

  ul {
    @include reset; /* 1 */


    /* Flexing items to get overflow ellipsis */

    @include flexbox {
      display: flex;
    }

  }


  /**
   * Styling list items
   *
   * 1. Set z-index to 0 so the :after psuedo element will be on top
   * 2. Make room for the psuedo element separator
   * 3. Make each item at least one breadcrumb size wide
   * 4. Hide by default so we can show only a few on mobile
   * 5. Hide overflowing content
   * 6. Proper spacing for non-flexbox use
   */

  li {
    position: relative; /* 1 */
    z-index: 0; /* 1 */
    padding-left: $breadcrumb-divider-size; /* 2 */
    min-width: $breadcrumb-size; /* 3 */
    display: none; /* 4 */
    overflow: hidden; /* 5 */
    vertical-align: middle; /* 6 */

    @include rtl {
      padding-left: 0;
      padding-right: $breadcrumb-divider-size;
    }


    /* Display on larger screens */

    @include breakpoint($breadcrumb-query) {
      display: inline-block;
    }


    /* Flexing items to get overflow ellipsis */

    @include flexbox($horizontal:$breadcrumb-query) {
      display: block;
      flex: 0 1 auto;
    }


    /* List item separator */

    @include psuedo(before, $content: false) {
      top: 0;
      bottom: 0;
      left: 0;
      width: $breadcrumb-divider-size;
      padding-top: $breadcrumb-size / 4;
      padding-bottom: $breadcrumb-size / 4;
      text-align: center;
      line-height: $breadcrumb-size / 2;
      content: $breadcrumb-divider-content;

      @include rtl {
        left: auto;
        right: 0;
      }
    }


    /* No separator on first item */

    &:first-child {
      padding-left: 0;

      @include rtl {
        padding-right: 0;
      }

      &:before {
        display: none;
      }
    }


    /**
     * Hover
     *
     * 1. Reset flexbox so the whole item is visible
     */

    &:hover {
      overflow: visible;

      @include flexbox {
        flex: 0 0 auto; /* 1 */
      }
    }
  }


  /**
   * Home and last elements
   *
   * 1. Always display these items
   * 2. No flexing for these items so the are readable
   */

  .k-breadcrumb__active,
  .k-breadcrumb__home {
    display: inline-block; /* 1 */
    overflow: visible; /* 1 */

    @include flexbox($horizontal:$breadcrumb-query) {
      flex: 0 0 auto; /* 2 */
    }
  }



  /**
   * Root element
   *
   * 1. Hide root text by default
   * 2. Show and position it when root is the active item to show more then just a home icon
   */

  .k-breadcrumb_root-text {
    @extend .visually-hidden; /* 1 */
  }

  .k-breadcrumb__home.k-breadcrumb__last {
    .k-breadcrumb_root-text {
      @extend .visually-shown; /* 2 */
      padding-left: $spacing-tiny; /* 2 */
      font-family: "Arial", sans-serif;

      @include rtl {
        padding-left: 0;
        padding-right: $spacing-tiny;
      }
    }
  }



  /**
   * One but last item
   *
   * Show this item on mobile to get a sense of where you are
   *
   * 1. Set 'missing' content
   * 2. Reset to default content on wider screens
   */

  li:nth-last-child(2) {
    display: inline-block;

    &:before {
      content: $breadcrumb-missing-content; /* 1 */

      @include breakpoint($breadcrumb-query) {
        content: $breadcrumb-divider-content; /* 2 */
      }
    }
  }


  /* Active item */

  .k-breadcrumb__last {
    .k-breadcrumb__item {
      font-weight: $bold;
    }
  }

}


/* Single breadcrumb items */

.k-breadcrumb__item {
  position: relative;
  z-index: 2;
  display: block;
  padding: round($breadcrumb-size / 4);
  min-width: $breadcrumb-size;
  min-height: $breadcrumb-size;
  overflow: hidden;
  text-align: center;
  line-height: floor($breadcrumb-size / 2);
  text-decoration: none;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.k-button {
  display: inline-block;
  margin-bottom: 0; // For input.k-button
  font-weight: $regular;
  text-align: center;
  vertical-align: middle;
  touch-action: manipulation;
  cursor: pointer;
  background-image: none; /* Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214 */
  border: 1px solid transparent;
  white-space: nowrap;
  box-shadow: none;
  text-shadow: none;
  padding: .35em .8em;
  font-size: $font-size;
  line-height: 21px;
  border-radius: $radius;
  user-select: none;
  margin-right: $spacing-tiny;

  &:last-child {
    margin-right: 0;
  }

  &,
  &:active,
  &.k-is-active {
    &:focus,
    &.focus {
      @include tab-focus;
    }
  }

  &:hover,
  &:focus {
    color: $btn-default-color;
    text-decoration: none;
  }

  &:active,
  &.k-is-active {
    outline: 0;
    background-image: none;
  }

  &[disabled],
  &.k-is-disabled {
    cursor: $cursor-disabled;
    opacity: .65;
  }
}

/**
 * 1. Remove underline when button is an `<a>` element
 * 2. Future-proof disabling of clicks on elements
 */

.k-button {
  &, &:hover, &:focus, &:active {
    text-decoration: none; /* 1 */
  }

  &.k-is-disabled {
    pointer-events: none; /* 2 */
  }
}


/* Alternate buttons */

.k-button--default {
  @include button-variant($btn-default-color, $btn-default-bg, $btn-default-border);
}

.k-button--primary {
  @include button-variant($btn-primary-color, $btn-primary-bg, $btn-primary-border);
}

.k-button--success {
  @include button-variant($btn-success-color, $btn-success-bg, $btn-success-border);
}

.k-button--info {
  @include button-variant($btn-info-color, $btn-info-bg, $btn-info-border);
}

.k-button--warning {
  @include button-variant($btn-warning-color, $btn-warning-bg, $btn-warning-border);
}

.k-button--danger {
  @include button-variant($btn-danger-color, $btn-danger-bg, $btn-danger-border);
}


/* Link buttons */
.k-button--link {
  color: $link-color;
  font-weight: normal;
  border-radius: 0;
  padding-left: 0;
  padding-right: 0;

  &,
  &:active,
  &.k-is-active,
  &[disabled],
  &.k-is-disabled {
    background-color: transparent;
    box-shadow: none;
  }

  &,
  &:hover,
  &:focus,
  &:active {
    border-color: transparent;
  }

  &:hover,
  &:focus {
    color: $link-hover-color;
    text-decoration: $link-hover-decoration;
    background-color: transparent;
  }

  &[disabled],
  &.k-is-disabled {
    &:hover,
    &:focus {
      color: $btn-link-disabled-color;
      text-decoration: none;
    }
  }
}


/* Block button */

.k-button--block {
  display: block;
  width: 100%;
}


/* Sizes */
.k-button--large {
  font-size: $font-size--medium;
  line-height: $line-height--medium;
}


.k-button--small {
  font-size: $font-size--small;
  line-height: $line-height--small;
}


.k-button--tiny {
  font-size: $font-size--tiny;
  line-height: $line-height--tiny;
}


/* Vertically space out multiple block buttons */

.k-button--block + .k-button--block {
  margin-top: 5px;
}


/* Specificity overrides */

input[type="submit"],
input[type="reset"],
input[type="button"] {
  &.k-button--block {
    width: 100%;
  }
}

.k-button-group {
  position: relative;
  display: inline-block;
  vertical-align: middle;

  & > .k-button {
    position: relative;
    float: left;
    margin: 0;

    /* Bring the "active" button to the front */
    &:hover,
    &:focus,
    &:active,
    &.k-is-active {
      z-index: 2;
    }
  }

  /* Prevent double borders when buttons are next to each other */
  .k-button + .k-button,
  .k-button + .k-button-group,
  .k-button-group + .k-button,
  .k-button-group + .k-button-group {
    margin-left: -1px;

    &:first-child {
      margin-left: 0;
    }
  }

  & > .k-button:not(:first-child):not(:last-child) {
    border-radius: 0;
  }

  & > .k-button:first-child:not(:last-child) {
    @include border-right-radius(0);
  }

  & > .k-button:last-child:not(:first-child) {
    @include border-left-radius(0);
  }

}

.k-card {
  width: 100%;
  margin-bottom: 1.5em;

  &:last-child {
    margin-bottom: 0;
  }
}

.k-card__body {
  @include transition;
  display: block;
  backface-visibility: visible;
  background: colorpicker(primary, 5);
  font-size: 14px;
  border: 1px solid #ddd;

  a#{&} {
    color: $color-accent; // This is the <a> so put color here
    cursor: pointer; // This is the <a> so put cursor here
  }
}

.k-card__body--preview {
  @include ratio-block(4 to 3);
}

.k-card__preview-content-wrapper {
  @include coverall(1px);
  backface-visibility: hidden;
  overflow: hidden;
}

.k-card__preview-content {
  width: 100%;
  height: 100%;
  max-height: 100%;
  overflow: hidden;
  vertical-align: middle;
  display: inline-block;
  text-align: center;
  font-size: 0;
  white-space: nowrap;
  padding: 1px; // space images with 1px

  &:before {
    content: ' ';
    display: inline-block;
    height: 100%;
    vertical-align: middle;
  }

  @include flexbox {
    display: flex;
    align-items: center;
    justify-content: center;
  }
}

.k-card__preview {
  display: inline-block;
  vertical-align: middle;
  text-align: center;
  max-height: 100%;
  max-width: 100%;
  overflow: hidden;
  font-size: $font-size;

  @include flexbox {
    flex: 0 0 auto;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  & > img,
  & > span {
    display: inline-block;
    max-height: 100%;
    vertical-align: middle;

    @include flexbox {
      flex: 0 1 auto;
    }
  }
}

.k-card__title {
  display: block;
  padding: 10px;
  width: 100%;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;

  label {
    font-weight: $regular;
    display: block;
    width: 100%;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    padding-left: 1px;
  }
}

.datepicker {
  font-family: $font-family;
  font-size: $font-size;
  line-height: $line-height;
  direction: ltr;

  &.datepicker-inline {
    width: 220px;
  }

  &.datepicker-rtl {
    direction: rtl;

    table tr td span {
      float: right;
    }
  }

  &.datepicker-dropdown {
    top: 0;
    left: 0;

    &:before {
      content: '';
      display: inline-block;
      border-left: 7px solid transparent;
      border-right: 7px solid transparent;
      border-bottom: 7px solid #ccc;
      border-top: 0;
      border-bottom-color: rgba(0, 0, 0, .2);
      position: absolute;
    }
    &:after {
      content: '';
      display: inline-block;
      border-left: 6px solid transparent;
      border-right: 6px solid transparent;
      border-bottom: 6px solid white;
      border-top: 0;
      position: absolute;
    }

    &.datepicker-orient-left:before {
      left: 6px;
    }

    &.datepicker-orient-left:after {
      left: 7px;
    }

    &.datepicker-orient-right:before {
      right: 6px;
    }

    &.datepicker-orient-right:after {
      right: 7px;
    }

    &.datepicker-orient-top:before {
      top: -7px;
    }

    &.datepicker-orient-top:after {
      top: -6px;
    }

    &.datepicker-orient-bottom:before {
      bottom: -7px;
      border-bottom: 0;
      border-top: 7px solid #999;
    }

    &.datepicker-orient-bottom:after {
      bottom: -6px;
      border-bottom: 0;
      border-top: 6px solid white;
    }
  }

  & > div {
    display: none;
  }

  &.days div.datepicker-days {
    display: block;
  }

  &.months div.datepicker-months {
    display: block;
  }

  &.years div.datepicker-years {
    display: block;
  }

  table {
    margin: 0;
    user-select: none;
  }

  td,
  th {
    text-align: center;
    width: 20px;
    height: 20px;
    border: none;
    border-radius: 4px;
  }

  // Inline display inside a table presents some problems with
  // border and background colors.
  .table-striped & table tr {
    td, th {
      background-color: transparent;
    }
  }

  table tr td {
    &.day:hover {
      background: $gray-lighter;
      cursor: pointer;
    }

    &.old,
    &.new {
      color: $gray-light;
    }

    &.disabled,
    &.disabled:hover {
      background: none;
      color: $gray-light;
      cursor: default;
    }

    &.today,
    &.today:hover,
    &.today.disabled,
    &.today.disabled:hover {
      $todayBackground: lighten(orange, 30%);
      background: $todayBackground;
      color: #000; // add last
    }

    &.today:hover:hover {
      // Thank bootstrap 2.0 for this selector...
      // TODO: Bump min BS to 2.1, use $textColor in buttonBackground above
      color: #000;
    }

    &.today.active:hover {
      color: #fff;
    }

    &.range,
    &.range:hover,
    &.range.disabled,
    &.range.disabled:hover {
      background: $gray-lighter;
      border-radius: 0;
    }

    &.range.today,
    &.range.today:hover,
    &.range.today.disabled,
    &.range.today.disabled:hover {
      $todayBackground: mix(orange, $gray-lighter, 50%);
      background: $todayBackground;
      border-radius: 0;
    }

    &.selected,
    &.selected:hover,
    &.selected.disabled,
    &.selected.disabled:hover {
      color: #fff;
      text-shadow: 0 -1px 0 rgba(0, 0, 0, .25);
      background: lighten($gray-light, 10);
    }

    &.active,
    &.active:hover,
    &.active.disabled,
    &.active.disabled:hover {
      color: #fff;
      text-shadow: 0 -1px 0 rgba(0, 0, 0, .25);
      background: $btn-primary-bg;
    }

    span {
      display: block;
      width: 23%;
      height: 54px;
      line-height: 54px;
      float: left;
      margin: 1%;
      cursor: pointer;
      border-radius: 4px;

      &:hover {
        background: $gray-lighter;
      }

      &.disabled,
      &.disabled:hover {
        background: none;
        color: $gray-light;
        cursor: default;
      }

      &.active,
      &.active:hover,
      &.active.disabled,
      &.active.disabled:hover {
        color: #fff;
        text-shadow: 0 -1px 0 rgba(0, 0, 0, .25);
        background: $btn-primary-bg;
      }

      &.old,
      &.new {
        color: $gray-light;
      }
    }
  }

  th.datepicker-switch {
    width: 145px;
  }

  thead tr:first-child th,
  tfoot tr th {
    cursor: pointer;
    &:hover {
      background: $gray-lighter;
    }
  }

  // Basic styling for calendar-week cells
  .cw {
    font-size: 10px;
    width: 12px;
    padding: 0 2px 0 5px;
    vertical-align: middle;
  }

  thead tr:first-child th.cw {
    cursor: default;
    background-color: transparent;
  }
}

.input-append,
.input-prepend {
  &.date {
    .add-on i {
      display: block;
      cursor: pointer;
      width: 16px;
      height: 16px;
    }
  }
}

.input-daterange {
  input {
    text-align: center;
  }

  input:first-child {
    border-radius: 3px 0 0 3px;
  }

  input:last-child {
    border-radius: 0 3px 3px 0;
  }

  .add-on {
    display: inline-block;
    width: auto;
    min-width: 16px;
    height: $line-height-base;
    padding: 4px 5px;
    font-weight: normal;
    line-height: $line-height-base;
    text-align: center;
    text-shadow: 0 1px 0 white;
    vertical-align: middle;
    background-color: $gray-lighter;
    border: 1px solid #ccc;
    margin-left: -5px;
    margin-right: -5px;
  }
}

/* Dropdown arrow/caret */

.k-caret {
  display: inline-block;
  width: 0;
  height: 0;
  margin-left: 2px;
  vertical-align: middle;
  border-top:   $caret-width-base dashed;
  border-right: $caret-width-base solid transparent;
  border-left:  $caret-width-base solid transparent;
}


/* The dropdown wrapper (div) */

.k-dropdown {
  position: relative;
}


/**
 * The dropdown menu (ul)
 *
 * 1. Hide by default, but block on "open" of the menu
 * 2. Ensures proper alignment if parent has it changed (e.g., modal footer)
 */

.k-dropdown__menu {
  position: absolute;
  top: 100%;
  left: 0;
  z-index: $zindex-dropdown;
  display: none; /* 1 */
  float: left;
  min-width: 160px;
  padding: 5px 0;
  margin: 2px 0 0;
  list-style: none;
  font-size: $font-size-base;
  text-align: left; /* 2 */
  background-color: $dropdown-bg;
  border: 1px solid $dropdown-border;
  border-radius: $border-radius-base;
  box-shadow: 0 6px 12px rgba(0,0,0,.175);
  background-clip: padding-box;


  /**
    * Links within the dropdown menu
    *
    * 1. prevent links from randomly breaking onto new lines
    */

  a {
    display: block;
    padding: 3px 20px;
    clear: both;
    font-weight: normal;
    line-height: $line-height-base;
    color: $dropdown-link-color;
    white-space: nowrap; /* 1 */
  }


  /* RTL */
  @include rtl {
    left: auto;
    right: 0;
  }
}



/* Dividers (basically an hr) within the dropdown */

.k-dropdown__divider {
  height: 1px;
  margin: (($line-height-computed / 2) - 1) 0;
  overflow: hidden;
  background-color: #e5e5e5;
}


/* Hover/Focus state */

.k-dropdown__menu a {
  &:hover,
  &:focus {
    text-decoration: none;
    color: $dropdown-link-hover-color;
    background-color: $dropdown-link-hover-bg;
  }
}


/* Active state */

.k-dropdown__menu .k-is-active a {
  &,
  &:hover,
  &:focus {
    color: $dropdown-link-active-color;
    text-decoration: none;
    outline: 0;
    background-color: $dropdown-link-active-bg;
  }
}

/**
 * Disabled state
 *
 * Gray out text and ensure the hover/focus state remains gray
 */

.k-dropdown__menu .k-is-disabled a {
  &,
  &:hover,
  &:focus {
    color: $dropdown-link-disabled-color;
  }

  /* Nuke hover/focus effects */
  &:hover,
  &:focus {
    text-decoration: none;
    background-color: transparent;
    background-image: none; // Remove CSS gradient
    cursor: $cursor-disabled;
  }
}


/* Open state for the dropdown */

.k-dropdown.k-is-open {

  /* Show the menu */
  & > .k-dropdown__menu {
    display: block;
  }

  /* Remove the outline when :focus is triggered */
  & > a {
    outline: 0;
  }
}


/* Backdrop to catch body clicks on mobile, etc. */

.k-dropdown-backdrop {
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  z-index: ($zindex-dropdown - 10);
}


/**
 * Dropup
 *
 * Allow for dropdowns to go bottom up (aka, dropup-menu)
 */

.k-dropdown--dropup {

  /* Reverse the caret */
  .k-caret {
    border-top: 0;
    border-bottom: $caret-width-base dashed;
    content: "";
  }

  /* Different positioning for bottom up menu */
  .k-dropdown__menu {
    top: auto;
    bottom: 100%;
    margin-bottom: 2px;
  }
}

.k-dropdown__menu--grid {
  width: 212px;
  position: absolute;
  padding: 5px;

  img {
    max-width: 16px;
    max-height: 16px;
  }

  li {
    float: left;
    width: 40px;
    height: 40px;
    margin: 0;
    padding: 0;
    display: block;

    @include rtl {
      float: right;
    }

    a {
      display: block;
      padding: 0;
      margin: 0;
      width: 40px;
      height: 40px;
      text-align: center;
      border-radius: 3px;
      float: left;

      [class^="k-icon-document-"],
      [class*=" k-icon-document-"] {
        display: block;
        vertical-align: middle;
        margin: ((40px - 16px)/2) auto;
      }
    }
  }

  .k-dropdown__block-item {
    min-width: 100%;
    clear: both;
    height: auto;
    margin: 5px 0 -5px;

    a {
      position: relative;
      height: auto;
      width: auto;
      float: none;
      border-top: 1px solid #e5e5e5;
      text-align: left;
      margin: 0 -5px;
      padding: 5px;
      border-radius: 0 0 $radius $radius;

      @include rtl {
        text-align: right;
      }
    }
  }
}

.k-empty-state {
  margin: 0;
  font-size: 1.5em;
  line-height: 1.5em;
  text-align: center;
  background: $no-items-background;
  color: $no-items-color;

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
    justify-content: center;
  }

  p {
    margin-bottom: 1em;

    &:last-child {
      margin-bottom: 0;
    }
  }
}

/* The block itself */

.k-form-block {
  @include border(all, $form-block-border);
  background: $form-block-background;
}


/* The header */

.k-form-block__header {
  @include border(bottom, $form-block-header-border);
  padding: $form-block-header-spacing;
  background: $form-block-header-background;
  font-weight: $bold;
}


/* The content */

.k-form-block__content {
  padding: $form-block-content-spacing;
}

.k-inline-form-group {
  display: inline-block;
  margin-right: 1em;

  &:last-child {
    margin-right: 0;
  }

  @include rtl {
    margin-right: 0;
    margin-left: 1em;

    &:last-child {
      margin-left: 0;
      margin-right: 1em;
    }
  }

  label {
    font-weight: $regular;
  }

  .k-input-group,
  .k-button-group {
    display: block;
  }
}

.k-form-row-group {
  background: #fff;
  border-radius: 5px;
  border: 1px solid #ccc;
  margin-bottom: $spacing;
}

.k-form-row {
  margin: 0;
  border-bottom: 1px solid #ccc;
  padding-bottom: 10px;

  @include breakpoint($beta) {
    display: table;
    width: 100%;
    padding-bottom: 0;

    @include rtl {
      direction: rtl;
    }

    &:last-child {
      border-bottom: none;
    }
  }

  .k-form-row__item {
    padding: 10px 10px 0 10px;
    border-right: 1px solid #ccc;

    @include rtl {
      border-right: none;
      border-left: 1px solid #ccc;
    }

    @include breakpoint($beta) {
      display: table-cell;
      vertical-align: middle;
      padding: 10px;
    }

    &:last-child {
      border-right: none;

      @include rtl {
        border-left: none;
      }
    }
  }

  .k-form-row__item--button,
  .k-form-row__item--label {
    width: 1%;
    white-space: nowrap;
  }

  .k-form-row__item--label {
    border-right: none;
    padding-right: 0;

    @include rtl {
      border-left: none;
      padding-right: 10px;
      padding-left: 0;
    }
  }

  .k-form-row__item--input {
    @include breakpoint($beta) {
      padding: 5px;
    }
  }

  .k-form-control {
    margin: 0;
  }

  .k-form-row__message {
    a {
      display: block;
      padding: 10px;

      &:focus,
      &:hover {
        background: #eee;
      }
    }
  }

  &:first-child {
    .k-form-row__message {
      a {
        border-radius: 5px 5px 0 0;
      }
    }
  }

  &:last-child {
    .k-form-row__message {
      a {
        border-radius: 0 0 5px 5px;
      }
    }
  }
}

/**
 * Shared styling between radio boxes and checkboxes
 */

@include modernizr('.k-appearance.k-checked') {
  input[type="checkbox"],
  input[type="radio"] {

    /**
     * 1. Resetting the default styling used by browsers. Don't do this for -moz-
          because the checkbox and radio box will look very ugly
     * 2. Positioning relative to position psuedo before element nicely
     * 3. Vertically positioning next to text
     * 4. Aligning vertically next to labels
     * 5. Indicate that element is clickable
     */

    -webkit-appearance: none; /* 1 */
    /* -moz-appearance: don't set this; /* 1 */
    position: relative; /* 2 */
    height: $form-option-size;
    width: $form-option-size;
    margin-top: -1px; /* 3 */
    margin-bottom: 2px; /* 3 */
    margin-left: 0;
    margin-right: .33em;
    border: $form-option-border-size solid $form-option-border;
    padding: 0;
    background: $form-option-background;
    display: inline-block;
    vertical-align: middle;
    cursor: pointer; /* 5 */
    box-shadow: $form-option-shadow;
    @include transition(background);

    /* Hover to indicate element is clickable */

    &:hover {
      background: #f1f1f1;
    }

    /* Set disabled border color a bit lighter so it won't appear to be larger */

    &:disabled {
      border-color: #bbb;
    }

    /**
     * 1. Position psuedo element absolute to have full control
     * 2. Position top-left, margin, width and height will take care of centering
     * 3. Initially hide the psuedo element
     * 4. Show the psuedo element when item is checked
     */

    &:before {
      position: absolute; /* 1 */
      top: 0; /* 2 */
      left: 0; /* 2 */
      visibility: hidden; /* 3 */
      transform: scale(0,0); /* 3 */
      text-align: center;
      vertical-align: middle;
      background: transparent;
      @include transition;
    }

    &:checked:before {
      visibility: visible; /* 4 */
      transform: scale(1,1); /* 4 */
    }

  }

  /* Put some space between the box and the label */
  label {

    /**
     * <input id="input1">
     * <label for="input1">Label 1</label>
     * <input id="input2">
     * <label for="input2">Label 2</label>
     */

    & + input[type="checkbox"],
    & + input[type="radio"] {
      margin-left: 1em;
    }

    /**
     * <label><input id="input1"> Label 1</label>
     * <label><input id="input2"> Label 2</label>
     */

    & + label {
      margin-left: 1em;
    }
  }
}


/* RTL */

@include modernizr-rtl('.k-appearance.k-checked') {
  input[type="checkbox"],
  input[type="radio"] {
    margin-left: .33em;
    margin-right: 0;

    &:before {
      left: auto;
      right: 0;
    }
  }

  label {

    & + input[type="checkbox"],
    & + input[type="radio"] {
      margin-left: auto;
      margin-right: 1em;
    }

    & + label {
      margin-left: auto;
      margin-right: 1em;
    }
  }
}



/* ==========================================================================
   CSS only styled checkbox
   ========================================================================== */

/**
 * 1. Remove rounded corners by default
 * 2. Display a check icon in the psuedo before element
 * 3. Height of the box minus the border size
 * 4. Height of the psuedo element minus a 1px padding/margin to move icon
      away from the side of the box
 * 5. Set the color of the check icon
 */

@include modernizr('.k-appearance.k-checked') {
  input[type="checkbox"] {
    border-radius: 0; /* 1 */

    @include icon-before(check, true) {
      /* 2 */
      width: $form-option-size - ($form-option-border-size * 2); /* 3 */
      height: $form-option-size - ($form-option-border-size * 2); /* 3 */
      line-height: $form-option-size - ($form-option-border-size * 2); /* 3 */
      font-size: $form-option-size - ($form-option-border-size * 4); /* 4 */
      color: $color-accent; /* 5 */
    }
  }
}



/* ==========================================================================
   CSS only styled radio box
   ========================================================================== */

/**
 * 1. Making the radio box round
 * 2. Adding content to display the psuedo element
 * 3. Small inner circle, scaling with the box itself
 * 4. positioning the inner circle
 * 5. Making inner circle actually round
 */

@include modernizr('.k-appearance.k-checked') {
  input[type="radio"] {
    border-radius: 50%; /* 1 */

    &:before {
      content: " "; /* 2 */
      width: $form-option-size - ($form-option-border-size * 8); /* 3 */
      height: $form-option-size - ($form-option-border-size * 8); /* 3 */
      margin: 3px; /* 4 */
      background: $form-option-color;
      border-radius: 50%; /* 5 */
    }
  }
}

/**
 * Common form controls
 * Shared size and type resets for form controls. Apply `.k-form-control` to any
 * of the following form controls:
 *
 * select
 * textarea
 * input[type="text"]
 * input[type="password"]
 * input[type="datetime"]
 * input[type="datetime-local"]
 * input[type="date"]
 * input[type="month"]
 * input[type="time"]
 * input[type="week"]
 * input[type="number"]
 * input[type="email"]
 * input[type="url"]
 * input[type="search"]
 * input[type="tel"]
 * input[type="color"]
 *
 * 1. Make inputs at least the height of their button counterpart (base line-height + padding + border)
 * 2. Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214
 * 3. Note: This has no effect on <select>s in some browsers, due to the limited stylability of <select>s in CSS.
 * 4. Customize the `:focus` state to imitate native WebKit styles.
 * 5. Unstyle the caret on `<select>`s in IE10+.
 * 6. iOS fix for unreadable disabled content; see https://github.com/twbs/bootstrap/issues/11655
 */

.k-form-control {
  display: block;
  width: 100%;
  height: $input-height-base; /* 1 */
  padding: $padding-base-vertical $padding-base-horizontal;
  font-size: 16px;
  line-height: $line-height-base;
  color: $input-color;
  background-color: $input-bg;
  background-image: none; /* 2 */
  border: 1px solid $input-border;
  border-radius: $input-border-radius; /* 3 */
  box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
  transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;


  /* 4 */

  @include form-control-focus;


  /* Placeholders */

  &::-moz-placeholder {
    color: $input-color-placeholder;
    opacity: 1;
  }

  &:-ms-input-placeholder {
    color: $input-color-placeholder;
  }

  &::-webkit-input-placeholder  {
    color: $input-color-placeholder;
  }

  /* 5 */
  &::-ms-expand {
    border: 0;
    background-color: transparent;
  }


  /* Disabled and read-only inputs */

  &[disabled],
  &[readonly],
  fieldset[disabled] & {
    background-color: $input-bg-disabled;
    opacity: 1; /* 6 */
  }

  &[disabled],
  fieldset[disabled] & {
    cursor: $cursor-disabled;
  }
}


/* Reset height for `textarea`s */

textarea.k-form-control {
  height: auto;
}


/**
 * Search inputs in iOS
 *
 * This overrides the extra rounded corners on search inputs in iOS so that our
 * `.k-form-control` class can properly style them. Note that this cannot simply
 * be added to `.k-form-control` as it's not specific enough. For details, see
 * https://github.com/twbs/bootstrap/issues/11586.
 */

input[type="search"] {
  -webkit-appearance: none;
}


/**
 * Special styles for iOS temporal inputs
 *
 * In Mobile Safari, setting `display: block` on temporal inputs causes the
 * text within the input to become vertically misaligned. As a workaround, we
 * set a pixel line-height that matches the given height of the input, but only
 * for Safari. See https://bugs.webkit.org/show_bug.cgi?id=139848
 *
 * Note that as of 8.3, iOS doesn't support `datetime` or `week`.
 */

@media screen and (-webkit-min-device-pixel-ratio: 0) {
  input[type="date"],
  input[type="time"],
  input[type="datetime-local"],
  input[type="month"] {
    &.k-form-control {
      line-height: $input-height-base;
    }

    .k-input-group--small & {
      line-height: $input-height-small;
    }

    .k-input-group--large & {
      line-height: $input-height-large;
    }
  }
}


/**
 * Form groups
 *
 * Designed to help with the organization and spacing of vertical forms. For
 * horizontal forms, use the predefined grid classes.
 */

.k-form-group {
  margin: 0 0 $spacing 0;

  &:last-child {
    margin-bottom: 0;
  }
}

/**
 * Static form control text
 *
 * Apply class to a `p` element to make any string of text align with labels in
 * a horizontal form layout.
 *
 * 1. Size it appropriately next to real form controls
 * 2. Remove default margin from `p`
 */

.k-static-form-label {
  display: inline-block;
  vertical-align: middle;
  max-width: 100%; /* 1 */
  margin-bottom: 5px;
  font-weight: bold;

  &:last-child {
    margin-bottom: 0;
  }
}


/**
 * Form control sizing
 *
 * Build on `.k-form-control` with modifier classes to decrease or increase the
 * height and font-size of form controls.
 *
 * The `.k-form-group-* k-form-control` variations are sadly duplicated to avoid the
 * issue documented in https://github.com/twbs/bootstrap/issues/15074.
 */

@include input-size('.input-sm', $input-height-small, $padding-small-vertical, $padding-small-horizontal, 16px, $line-height-small, $input-border-radius-small);
.k-form-group--small {
  .k-form-control {
    height: $input-height-small;
    padding: $padding-small-vertical $padding-small-horizontal;
    font-size: $font-size-small;
    line-height: $line-height-small;
    border-radius: $input-border-radius-small;
  }

  select.k-form-control {
    height: $input-height-small;
    line-height: $input-height-small;
  }

  textarea.k-form-control,
  select[multiple].k-form-control {
    height: auto;
  }
}

@include input-size('.input-lg', $input-height-large, $padding-large-vertical, $padding-large-horizontal, $font-size-large, $line-height-large, $input-border-radius-large);
.k-form-group--large {
  .k-form-control {
    height: $input-height-large;
    padding: $padding-large-vertical $padding-large-horizontal;
    font-size: $font-size-large;
    line-height: $line-height-large;
    border-radius: $input-border-radius-large;
  }

  select.k-form-control {
    height: $input-height-large;
    line-height: $input-height-large;
  }

  textarea.k-form-control,
  select[multiple].k-form-control {
    height: auto;
  }
}

.k-has-success {
  @include form-control-validation($state-success-text, $state-success-text, $state-success-bg);
}
.k-has-warning {
  @include form-control-validation($state-warning-text, $state-warning-text, $state-warning-bg);
}
.k-has-error {
  @include form-control-validation($state-danger-text, $state-danger-text, $state-danger-bg);
}

/**
 * 1. For dropdowns
 * 2. Prevent input groups from inheriting border styles from table cells when placed within a table
 * 3. Ensure that the input is always above the *appended* addon button for proper border colors.
 * 4. IE9 fubars the placeholder attribute in text inputs and the arrows on
      select elements in input groups. To fix it, we float the input.
 * 5. Put focused element on top of the rest
 */

.k-input-group {
  position: relative; /* 1 */
  display: table;
  border-collapse: separate; /* 2 */

  .k-form-control {

    /* 3 */
    position: relative;
    z-index: 2;

    /* 4 */
    float: left;

    width: 100%;
    margin-bottom: 0;

    &:focus {
      z-index: 3; /* 5 */
    }
  }
}


/* Sizes */

.k-input-group--large > .k-form-control,
.k-input-group--large > .k-input-group__addon,
.k-input-group--large > .k-input-group__button > .k-button {
  @extend .input-lg;
}
.k-input-group--small > .k-form-control,
.k-input-group--small > .k-input-group__addon,
.k-input-group--small > .k-input-group__button > .k-button {
  @extend .input-sm;
}

/* Input group parts */

.k-input-group__addon,
.k-input-group__button,
.k-input-group .k-form-control {
  display: table-cell;

  &:not(:first-child):not(:last-child) {
    border-radius: 0;
  }
}


/**
 * Addon and addon wrapper for buttons
 *
 * 1. Match the inputs
 */

.k-input-group__addon,
.k-input-group__button {
  width: 1%;
  white-space: nowrap;
  vertical-align: middle; /* 1 */
}


/* Text addon */

.k-input-group__addon {
  padding: $padding-base-vertical $padding-base-horizontal;
  font-size: $font-size-base;
  font-weight: normal;
  line-height: 1;
  color: $input-color;
  text-align: center;
  background-color: $input-group-addon-bg;
  border: 1px solid $input-group-addon-border-color;
  border-radius: $input-border-radius;


  /* Sizing */

  &.input-sm {
    padding: $padding-small-vertical $padding-small-horizontal;
    font-size: $font-size-small;
    border-radius: $input-border-radius-small;
  }

  &.input-lg {
    padding: $padding-large-vertical $padding-large-horizontal;
    font-size: $font-size-large;
    border-radius: $input-border-radius-large;
  }


  /* Nuke default margins from checkboxes and radios to vertically center within. */

  input[type="radio"],
  input[type="checkbox"] {
    margin-top: 0;
  }
}


/* Reset rounded corners */
.k-input-group .k-form-control:first-child,
.k-input-group__addon:first-child,
.k-input-group__button:first-child > .k-button,
.k-input-group__button:first-child > .k-button-group > .k-button,
.k-input-group__button:first-child > .k-dropdown__toggle,
.k-input-group__button:last-child > .k-button:not(:last-child):not(.k-dropdown__toggle),
.k-input-group__button:last-child > .k-button-group:not(:last-child) > .k-button {
  @include border-right-radius(0);
}

.k-input-group__addon:first-child {
  border-right: 0;
}

.k-input-group .k-form-control:last-child,
.k-input-group__addon:last-child,
.k-input-group__button:last-child > .k-button,
.k-input-group__button:last-child > .k-button-group > .k-button,
.k-input-group__button:last-child > .k-dropdown__toggle,
.k-input-group__button:first-child > .k-button:not(:first-child),
.k-input-group__button:first-child > .k-button-group:not(:first-child) > .k-button {
  @include border-left-radius(0);
}

.k-input-group__addon:last-child {
  border-left: 0;
}


/**
 * Button input groups
 *
 * 1. Jankily prevent input button groups from wrapping with `white-space` and
      `font-size` in combination with `inline-block` on buttons.
 * 2. Negative margin for spacing, position for bringing hovered/focused/actived
      element above the siblings.
 * 3. Bring the "active" button to the front
 * 4. Negative margin to only have a 1px border between the two
 */

.k-input-group__button {
  position: relative;

  /* 1 */
  font-size: 0;
  white-space: nowrap;

  /* 2 */
  > .k-button {
    position: relative;

    + .k-button {
      margin-left: -1px;
    }

    /* 3 */
    &:hover,
    &:focus,
    &:active {
      z-index: 2;
    }
  }

  /* 4 */
  &:first-child {
    > .k-button,
    > .k-button-group {
      margin-right: -1px;
    }
  }

  &:last-child {
    > .k-button,
    > .k-button-group {
      z-index: 2;
      margin-left: -1px;
    }
  }
}

.k-gallery-container {
  background: $grid-background;

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    overflow: auto;
  }
}

.k-gallery {
  background: $grid-background;
  padding: $grid-spacing / 2;
  overflow: auto;

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
  }
}

.k-gallery__items {
  @include clearfix;

  @include flexbox {
    flex: 1 1 auto;
  }
}

.k-gallery__item {
  float: left;
  padding: $grid-spacing / 2;
  width: 100%;

  @include rtl {
    float: right;
  }

  // Set breakpoints
  @include setGridBreakpoint;

  // Selected styling
  &.k-is-selected {
    .k-card__title {
      background: $color-accent;
      border-color: darkest($color-accent);
      color: $color-accent-type;

      a {
        color: $color-accent-type;
      }
