/**
 * 1. Reset default <ul> styling
 */

.k-navigation {
  @include reset($padding: false, $background: false); /* 1 */
  @include border(bottom, $navigation-border);
  padding: ($navigation-spacing * 2) 0;
  background: $navigation-background;


  /* Items */

  li {
    display: block;
    line-height: $line-height;
  }


  /**
   * Links
   *
   * 1. Make item fully clickable
   * 2. removing underlines
   */

  a {
    display: block; /* 1 */
    padding-top: $navigation-spacing;
    padding-bottom: $navigation-spacing;
    padding-right: round($navigation-spacing * 2);
    padding-left: round($navigation-spacing * 3);
    font-size: $navigation-font-size;
    color: $navigation-color;
    text-decoration: none; // 2

    @include rtl {
      padding-right: round($navigation-spacing * 3);
      padding-left: round($navigation-spacing * 2);
    }


    /* Hover & focus state */

    &:hover,
    &:focus {
      background: $list-hover-background;
      color: $list-hover-color;
      text-decoration: none; /* 2 */
    }


    /**
     * Faux border
     *
     * Adding a border to the left, using "after" psuedo since "before" is used for the icon
     */

    @include psuedo(after) {
      left: 0;
      top: 0;
      width: $navigation-border-width;
      height: 100%;

      @include rtl {
        left: auto;
        right: 0;
      }
    }


    /* Hover & focus states */

    &:hover,
    &:focus {
      background: $navigation-hover-background;

      &:after {
        background: $navigation-hover-border;
      }
    }
  }


  /* Active link */

  .k-is-active {
    a {
      background: $navigation-active-background;
      color: $navigation-active-color;
      font-weight: $bold;

      &:after {
        top: 12%;
        height: 76%;
        background: $navigation-active-border;
      }

      &:hover {
        background: transparent;
        cursor: default;
      }
    }
  }
}

/**
 * Check if :checked is supported using modernizr
 */

@include modernizr('.k-checked') {
  .k-optionlist {
    position: relative;
    z-index: 1;
    display: table;
    margin: 0;
    border: 1px solid $input-border;
    padding: 1px;
    width: 100%;
    height: $input-height-base;
    border-radius: $input-border-radius;
    background: white;

    /* Hiding the input but keeping accessible */

    input[type=radio] {
      @extend .visually-hidden;
    }


    /**
     * Label styling
     *
     * 1. Display as table cell so the labels line up next to each other
     * 2. Set height to input height minus border and spacing
     * 3. Take one pixel off the radius to look smooth
     * 4. Don't let the user select
     */

    label {
      position: relative;
      z-index: 1;
      display: table-cell; /* 1 */
      margin: 0;
      padding: 0;
      height: $input-height-base - 4px; /* 2 */
      line-height: $input-height-base - 4px; /* 2 */
      text-align: center;
      color: $input-color;
      font-weight: normal;
      border-radius: $input-border-radius - 1px; /* 3 */
      cursor: pointer;
      user-select: none; /* 4 */
      @include transition;


      /* A little darker background on hovering */

      &:hover {
        background: rgba(0, 0, 0, .15);
      }
    }


    /**
     * Styling label when checked
     *
     * 1. White text on colored labels
     * 2. Display colored background when item is :checked
     */

    input:checked + label {
      background: $color-neutral;
      color: $color-neutral-type;
    }


    /**
     * Fake focus
     *
     * We need to create a separate element for the fake focus since we're dealing with more than two items here
     *
     * 1. Position it on top of the parent element but behind the labels
     * 2. Hide by default
     * 3. Display on focus
     * 4. Set border-radius same as parent
     */

    .k-faux-focus {
      position: absolute; /* 1 */
      top: -1px; /* 1 */
      right: -1px; /* 1 */
      bottom: -1px; /* 1 */
      left: -1px; /* 1 */
      z-index: 0; /* 1 */
      outline: 0;
      display: block;
      opacity: 0; /* 2 */
      visibility: hidden; /* 2 */
      border-radius: $input-border-radius; /* 4 */
      border: 1px solid $input-border-focus;
      box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba($input-border-focus, 0.6);
    }

    input:focus ~ .k-faux-focus {
      opacity: 1; /* 3 */
      visibility: visible; /* 3 */
    }
  }
}

/**
 * Check if :checked is supported using modernizr
 */

@include modernizr('.k-checked') {
  .k-booleanlist {
    position: relative;
    z-index: 1;
    display: block;
    margin: 0;
    border: 1px solid $input-border;
    padding: 1px;
    width: 100%;
    height: $input-height-base;
    border-radius: $input-border-radius;
    background: white;


    /* Hiding the input but keeping accessible */

    input[type="radio"] {
      @extend .visually-hidden;
    }


    /**
     * Label styling
     *
     * 1. Float left so the two labels line up next to each other
     * 2. Display block so we have styling control
     * 3. Set height to input height minus border and spacing
     * 4. Take up half of the parent
     * 5. Take one pixel off the radius to look smooth
     * 6. Don't let the user select
     */

    label {
      float: left; /* 1 */
      display: block; /* 2 */
      margin: 0;
      padding: 0;
      height: $input-height-base - 4px; /* 3 */
      line-height: $input-height-base - 4px; /* 3 */
      width: 50%; /* 4 */
      text-align: center;
      color: $input-color;
      font-weight: normal;
      border-radius: $input-border-radius - 1px; /* 5 */
      cursor: pointer;
      user-select: none; /* 6 */
      @include transition;


      /**
       * Colored backgrounds
       *
       * 1. Position absolute to place behind the label
       * 2. Set background behind the text on the label but on top of the parent
       * 3. Respect the 1px padding set on the parent
       * 4. Set to black and set content to display psuedo element
       * 5. Hide by default, using opacity for performance reasons
       * 6. Take one pixel off the radius to look smooth
       */

      &:before {
        position: absolute; /* 1 */
        z-index: -1; /* 2 */
        top: 1px; /* 3 */
        bottom: 1px; /* 3 */
        display: block; /* 4 */
        content: " "; /* 4 */
        opacity: 0; /* 5 */
        border-radius: $input-border-radius - 1px; /* 6 */
        @include transition;
      }


      /**
       * Faking :focus on parent
       *
       * 1. Position absolute to parent
       * 2. Set below label itself but above parent
       * 3. Set 1px back because of padding on parent element
       * 4. Hide by default, using opacity for performance reasons
       * 5. Set to black and set content to display psuedo element
       * 6. Same border-radius as parent
       * 7. Same "focus" styling as input elements
       */

      &:after {
        position: absolute; /* 1 */
        z-index: -1; /* 2 */
        top: -1px; /* 3 */
        right: -1px; /* 3 */
        bottom: -1px; /* 3 */
        left: -1px; /* 3 */
        opacity: 0; /* 4 */
        display: block; /* 5 */
        content: " "; /* 5 */
        border-radius: $input-border-radius; /* 6 */
        border: 1px solid $input-border-focus; /* 7 */
        box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba($input-border-focus, 0.6); /* 7 */
      }


      /* A little darker background on hovering */

      &:hover {
        background: rgba(0,0,0,.15);
      }

    }


    /**
     * Styling label on hover, focus and checked
     *
     * 1. White text on colored labels
     * 2. Display colored background when item is :checked
     * 3. Show fake :focus when any item has :active or :focus
     */

    input {
      &:checked + label {
        color: white; /* 1 */

        &:before {
          opacity: 1; /* 2 */
        }
      }

      &:active + label,
      &:focus + label {
        &:after {
          opacity: 1; /* 3 */
        }
      }
    }


    /**
     * Positioning the colored backgrounds
     *
     * We're not using transform here since an 'overflow:hidden' on the parent would
     * make our fake :focus disappear
     *
     * 1. Set correct color to item
     * 2. Start with 0% (not 0) to have smooth transitions
     * 3. Set position to half so the background takes only up half the space of the parent
     */

    input:first-of-type + label {
      &:before {
        right: 0%; /* 2 */
        left: 50%; /* 3 */
      }
    }

    input:last-of-type + label {
      &:before {
        right: 50%; /* 3 */
        left: 0%;  /* 2 */
      }
    }

    input[value="1"] + label {
      &:before {
        background: $radio-toggle-1; /* 1 */
      }
    }

    input[value="0"] + label {
      &:before {
        background: $radio-toggle-0; /* 1 */
      }
    }

    &--neutral {
      input[value] + label {
        &:before {
          background: $radio-toggle-neutral; /* 1 */
        }
      }
    }


    /**
     * Positioning the colored backgrounds when :checked
     *
     * 1. 1px to account for padding in parent element
     * 2. Set position to half so the background takes only up half the space of the parent
     */

    input:checked:first-of-type + label {
      &:before {
        left: 1px; /* 1 */
        right: 50%; /* 2 */
      }
    }

    input:checked:last-of-type + label {
      &:before {
        right: 1px; /* 1 */
        left: 50%; /* 2 */
      }
    }
  }
}

@include modernizr-rtl('.k-checked') {
  .k-booleanlist {
    direction: rtl;

    label {
      float: right;
    }

    input:first-of-type + label {
      &:before {
        right: 50%;
        left: 0%;
      }
    }

    input:last-of-type + label {
      &:before {
        right: 0%;
        left: 50%;
      }
    }

    input:checked:first-of-type + label {
      &:before {
        left: 50%;
        right: 1px;
      }
    }

    input:checked:last-of-type + label {
      &:before {
        right: 50%;
        left: 1px;
      }
    }
  }
}

// @TODO: refactor this html on top and show some of the options or don't make options available at all


.k-scopebar {
  background: $scopebar-background;
  color: $scopebar-color;
  min-width: 100%;

  @include breakpoint($delta) {
    display: table;
    width: 100%;
  }


  /**
   * When flexbox is in use
   */

  @include flexbox {
    flex: 0 0 auto;
  }

  @include flexbox($horizontal: $delta) {
    display: flex;
    width: auto;
    flex: 0 0 auto;
  }


  /**
   * Breadcrumbs in the scopebar
   *
   * When the breadcrumbs are in the scopebar the breadcrumbs should be flexing
   */

  .k-breadcrumb {
    flex: 1 1 auto;
  }
}



/**
 * Items
 */

.k-scopebar__item {
  @include border(bottom, $scopebar-border);
  vertical-align: middle;


  /**
   * When flexbox is in use
   */

  @include flexbox($horizontal: $delta) {
    display: block;
    flex: 0 0 auto;


    /* Search item */

    &.k-scopebar__item--search {
      display: block;
      flex: 0 0 auto;
    }
  }
}



/**
 * Fluid item
 *
 * Put at least one fluid item in every scopebar for flexbox to work correctly
 */

.k-scopebar__item--fluid {
  @include clearfix;

  @include breakpoint($delta) {
    display: table-cell;
    width: 99%;
  }

  @include flexbox($horizontal: $delta) {
    flex: 1 1 auto;
    width: auto;
  }
}




/**
 * Filters
 *
 * 1. Clearfix so we can float individual filters
 * 2. For positioning dropdowns on smaller screens
 */

.k-scopebar__filters {
  @include clearfix; /* 1 */
  position: relative; /* 2 */
  z-index: 2; /* 2 */
}


/* Individual filter */

.k-scopebar__item--filter {
  display: block;
  float: left;
  width: auto;
  line-height: $scopebar-size;
  @include border(right, #ccc);
  @include border(bottom, #ccc);
  margin-bottom: -1px;
  height: $scopebar-size + 1px;
  font-size: $scopebar-filter-font-size;

  @include breakpoint($beta) {
    position: relative;
  }

  @include rtl {
    border-right: none;
    @include border(left, #ccc);
    float: right;
  }
}


/* Filter title */

.k-scopebar__item--filter__title,
.k-scopebar__item--filter__icon {
  display: inline-block;
  vertical-align: middle;
}

.k-scopebar__item--filter__icon {
  margin-left: 2px;

  @include rtl {
    margin-left: 0;
    margin-right: 2px;
  }

  @include breakpoint($beta) {
    margin-left: 4px;

    @include rtl {
      margin-left: 0;
      margin-right: 4px;
    }
  }
}


/* Scopebar label showing how many active filters you have below a filter button */

.k-scopebar__item__label {
  position: absolute;
  z-index: 2;
  top: 2px;
  right: 2px;
  min-width: 15px;
  height: 15px;
  border-radius: 50%;
  background: #d35400;
  color: white;
  font-size: 10px;
  line-height: 15px;
  text-align: center;
  font-weight: $bold;
  box-shadow: inset 0 0 2px rgba(0,0,0,.1);
  pointer-events: none;

  @include rtl {
    right: auto;
    left: 2px;
  }

  @include breakpoint($beta) {
    top: 6px;
    right: 4px;

    @include rtl {
      right: auto;
      left: 4px;
    }
  }

  &--numberless {
    &:after {
      display: block;
      content: " ";
      width: 5px;
      height: 5px;
      background: white;
      position: absolute;
      top: 5px;
      left: 5px;
      border-radius: 50%;
      box-shadow: 1px 1px 2px rgba(0,0,0,.2);
    }
  }
}



/**
 * Search
 *
 * 1. Hide by default (when JS is enabled) so we can toggle it open
 * 2. Display as table cell on medium sized screens (!important so it displays even if it's hidden by JS)
 * 3. Display as block for flexbox on larger screens
 * 5. Search area items
 */

.k-scopebar__item--search {
  position: relative;

  @include breakpoint($delta) {
    @include allwidth(220px);
  }

  @include breakpoint($echo) {
    @include allwidth(280px);
  }

  @include js-enabled {
    display: none; /* 1 */

    @include breakpoint($delta) {
      display: table-cell!important; /* 2 */
    }
  }


  /* 5 */

  .k-search__container {
    width: 100%;
    position: relative;
  }

  .k-search__container--has-search-button .k-search__field {
    padding-left: 28px;

    @include rtl {
      padding-left: 0;
      padding-right: 28px;
    }
  }

  .k-search__container--has-empty-button .k-search__field {
    padding-right: 18px;

    @include rtl {
      padding-right: 0;
      padding-left: 28px;
    }
  }

  .k-search__container--has-both-buttons .k-search__field {
    padding-left: 28px;
    padding-right: 18px;

    @include rtl {
      padding-left: 0;
      padding-right: 28px;
    }
  }

  label {
    @extend .visually-hidden;
  }

  .k-search__field {
    @include reset;
    margin: 0;
    display: block;
    width: 100%;
    height: $scopebar-size;
    border-radius: 0;
    padding: $spacing-tiny;
    padding-right: $scopebar-size;
    background: $scopebar-search-background;
    color: $scopebar-search-color;
    box-shadow: none;
    font-size: 12px;
    -webkit-appearance: none;

    @include rtl {
      padding-right: 0;
      padding-left: $scopebar-size;
    }

    @include breakpoint($delta) {
      @include border(left, $scopebar-border);
      @include border(bottom, $scopebar-border);
      height: $scopebar-size + 1px;
      margin-bottom: -1px;

      @include rtl {
        border-left: none;
        @include border(right, $scopebar-border);
      }
    }
  }

  .k-search__button-search {
    @include reset;
    position: absolute;
    top: 50%;
    left: .5em;
    margin-top: -.75em;

    @include rtl {
      left: auto;
      right: .5em;
    }
  }

  .k-search__button-empty {
    border: none;
    margin: 0;
    padding: 0;
    position: absolute;
    display: none;
    top: 50%;
    right: 0;
    width: 22px;
    height: 22px;
    margin-top: -10px;
    background: transparent;

    @include rtl {
      right: auto;
      left: 0;
    }

    &.k-is-visible {
      display: inline-block;
    }

    span {
      margin: 3px;
      -webkit-box-sizing: border-box;
      -moz-box-sizing: border-box;
      box-sizing: border-box;
      width: 16px;
      height: 16px;
      display: inline-block;
      line-height: 16px;
      text-align: center;
      border-radius: 100%;
      color: #777;
      text-decoration: none;
      font-weight: $bold;
      font-family: Arial, helvetica, sans-serif;
      font-size: 10px;
    }

    &:hover {
      cursor: pointer;

      span {
        background: #ea9393;
        border: 1px solid #ce4c4c;
        color: #fff;
        text-shadow: 1px 1px 1px #ce4c4c;
        box-shadow: inset 1px 2px 1px lighten(#ce4c4c, 10%);
      }
    }
  }

  // The active search label
  .k-scopebar__item__label--numberless {
    position: absolute;
    right: auto;
    left: 13px;
    top: 5px;

    @include rtl {
      left: auto;
      right: 13px;
    }
  }
}



/**
 * Search toggle button
 *
 * 1. Hide when search is visible
 */

.k-scopebar-icon-button {
  position: relative;
  display: none;
  width: $scopebar-size;
  height: 0;
  margin: 0;
  border: none;
  padding: 0;
  padding-top: $scopebar-size;
  background: transparent;

  [class^="k-icon"] {
    position: absolute;
    top: 50%;
    left: 50%;
    font-size: 16px;
    vertical-align: middle;
    margin: -8px 0 0 -8px;

    @include rtl {
      left: auto;
      right: 50%;
      margin: -8px -8px 0 0;
    }
  }

  @include js-enabled {
    display: block;
  }
}


.k-scopebar__search-toggle-wrapper {
  width: $scopebar-size;
  max-width: $scopebar-size;
  height: $scopebar-size;
  white-space: nowrap;
}


.k-scopebar__filters-wrapper {
  display: table;
  width: 100%;
  height: auto;
}

.k-scopebar__filters-content,
.k-scopebar__search-toggle-wrapper {
  display: table-cell;
  vertical-align: top;
}

.k-scopebar__search-toggle-wrapper {
  @include breakpoint($delta) {
    display: none;
  }
}

.k-scopebar__filters-content {
  min-height: $scopebar-size;
  min-width: $scopebar-size;
  width: 99%;
}

.k-scopebar__filters {
  @include js-enabled {
    display: none;

    @include breakpoint($beta) {
      display: block !important;
    }
  }
}


.k-toggle-filters {
  @include js-enabled {
    display: block;

    @include breakpoint($beta) {
      display: none;
    }
  }
}

.k-toggle-search {
  @include js-enabled {
    display: block;
    float: right;

    @include rtl {
      float: left;
    }
  }
}



/**
 * Right floating button group
 */

.k-scopebar__group--right {
  float: right;

  @include rtl {
    float: left;
  }
}

.k-scopebar__group--right__item {
  float: left;
  height: $scopebar-size;

  @include rtl {
    float: right;
  }
}

.k-scopebar-dropdown {
  overflow: hidden;

  @include breakpoint($beta) {
    position: relative;
    z-index: 2;
  }

  &.k-is-active {
    overflow: visible;
    z-index: 3;

    .k-scopebar-dropdown__body {
      visibility: visible;
    }

    .k-scopebar__item--filter__title {
      text-decoration: underline;
    }
  }

  &.k-is-active {
    .k-scopebar-dropdown__button {
      background: $scopebar-buttons-background-hover;
    }
  }
}

.k-scopebar-dropdown__button {
  position: relative;
  border: none;
  background: transparent;
  margin: 0;
  padding: 0 6px;

  &:hover,
  &:focus {
    background: $scopebar-buttons-background-hover;
  }

  @include breakpoint($beta) {
    padding: 0 12px;
  }
}

.k-scopebar-dropdown__body {
  position: absolute;
  visibility: hidden;
  background: $dropdown-background;
  @include border(all, $dropdown-border);
  color: $dropdown-color;
  box-shadow: 2px 2px 3px rgba(0,0,0,.15);
  border-radius: 0 0 5px 5px;
  min-width: 200px;
  left: 0;
  right: 0;
  top: 100%;

  @include breakpoint($beta) {
    min-width: 300px;
    right: auto;
    left: -1px;

    @include rtl {
      left: auto;
      right: -1px;
    }
  }

  .select2 {
    min-width: 100%;
  }
}

.k-scopebar-dropdown__body__content {
  padding: 5px;
}

.k-scopebar-dropdown__body__buttons {
  line-height: 14px;
  @include border(top, $dropdown-buttons-border);
  background: $dropdown-buttons-background;
  color: $dropdown-buttons-color;
  padding: 8px 5px;
  text-align: center;
  border-radius: 0 0 5px 5px;

  .btn {
    padding: 4px 30px;
    line-height: 16px;
  }
}

.k-scopebar__button {
  @include reset;
  display: block;
  float: left;
  height: round($scopebar-size * .8);
  margin: round($scopebar-size * .1);
  margin-left: 0;
  padding-left: round((($scopebar-size * .8) - (2px + 16px)) / 2);
  padding-right: round((($scopebar-size * .8) - (2px + 16px)) / 2);
  border-radius: 2px;
  text-align: center;
  line-height: round($scopebar-size * .8);
  cursor: pointer;
  @include transition;
  @include border(all, transparent);

  @include rtl {
    float: right;
    margin-left: auto;
    margin-right: 0;
  }

  &:hover {
    border-color: $scopebar-border;
    background: lighter($scopebar-background);
  }

  &.k-is-active {
    display: none;
  }

  [class^="k-icon"] {
    display: block;
    margin-top: round((($scopebar-size * .8) - (2px + 16px)) / 2); // absolute center
    margin-bottom: round((($scopebar-size * .8) - (2px + 16px)) / 2); // absolute center
    width: 16px;
    height: 16px;
    line-height: 0;
    font-size: 0;
    vertical-align: middle;

    &:before {
      font-size: 16px;
      line-height: 16px;
    }
  }
}

/**
 * 100% wide by default instead of just 220px making it responsive
 * Important because width is being added by JS as inline styling
 */

.select2-container.select2-container--bootstrap {
  width: 100% !important;
  max-width: 100% !important;


  /* Set proper line-height so text is vertically centered */

  .select2-selection--single {
    .select2-selection__rendered {
      line-height: $line-height-base;
    }
  }


  /* Set proper height so arrow is vertically centered */

  .select2-selection--single {
    .select2-selection__arrow {
      height: $input-height-base;
    }
  }

  /* Set proper styling for the search input field */

  .select2-search__field {
    height: $input-height-base;
    margin: 0;
  }
}

.k-sidebar-item {
  & + & {
    margin-top: $spacing;
  }

  @include flexbox {
    flex: 0 0 auto;

    &--flex {
      flex: 1 0 auto;
    }
  }

  /**
   * Overflowing items
   *
   * 1. Set container to overflow it's items
   */

  &--overflow {
    overflow: auto; /* 1 */
    width: 100%;

    @include flexbox {
      flex: 0 1 auto;
    }

    @include resizing {
      overflow: hidden;
    }


    /**
     * Box shadow for overflowing containers
     *
     * 1. Set as same color as background to make it look like text is fading
     */

    & ~ [class^="k-overflowing"] {
      color: $sidebar-background;
    }
  }
}


.k-sidebar-item--toggle {
  padding-bottom: $spacing-small;

  .k-list {
    @include js-enabled {
      @media (min-height: $flexbox-vertical) {
        display: none;
      }
    }

    @media (min-height: 550px) {
      display: block!important;
    }
  }

  .k-sidebar-item__header {
    position: relative;
  }

  .k-sidebar-item__toggle {
    position: absolute;
    top: $spacing;
    right: $spacing;
    display: none;

    @include icon-after(chevron-top, true) {
      display: block;
      background: white;
      border: 1px solid #ddd;
      border-radius: 10px;
      width: 20px;
      height: 20px;
      text-align: center;
      line-height: 20px;
      vertical-align: middle;
      font-size: 10px;
      cursor: pointer;
      @include transition;
    }

    &.k-is-active:after {
      transform: rotate(180deg);
    }

    @media (min-height: $flexbox-vertical) {
      display: block;
    }

    @media (min-height: 550px) {
      display: none;
    }

    @include rtl {
      right: auto;
      left: $spacing;
    }
  }
}

.k-sidebar-item__header {
  padding: $spacing $spacing 0;
  font-weight: $bold;
  text-transform: uppercase;
  font-size: 12px;
}

.k-sidebar-item__content {
  padding: $spacing;
}

.k-sidebar-item__content--small {
  padding: $spacing-small $spacing;
}

.k-table-container {
  background: $table-background;

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }
}


.k-table {
  overflow: auto;

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    background: $table-cell-background-even;
  }
}


/* Default table settings */

.k-table-container {
  table {
    width: 100%;
    text-align: left;
    border-collapse: separate;
    border-spacing: 0;

    @include rtl {
      text-align: right;
    }

    @include flexbox {
      flex: 1 1 auto;
    }
  }


  /**
   * Cells
   *
   * 1. Position checkbox
   * 2. Remove default margin and set negative top margin to visually align vertically
   */

  th,
  td {
    border: none;
    position: relative;
    height: $table-cell-size;
    vertical-align: middle;
    text-align: left;
    @include transition;

    input {
      display: inline-block; /* 1 */
      vertical-align: middle; /* 1 */
      margin: -1px 0 0 0; /* 2 */
    }
  }

  @include rtl {
    th,
    td {
      text-align: right;
    }
  }


  /**
   * Head
   *
   * 1. Don't set a vertical padding on table headers. Set padding on table heading children
   *    We do this so <a> elements are fully clickable for sorting
   * 2. Don't use long header titles since they won't be wrapped
   * 3. Whole <th> should be clickable
   * 4. Set padding here
   */

  th {
    @include border(bottom, $table-header-border);
    z-index: 1;
    padding: 0 $table-cell-spacing-horizontal; /* 1 */
    white-space: nowrap; /* 2 */
    background: $table-header-background;
    font-weight: $regular;

    a {
      display: block; /* 3 */
      padding: $table-cell-spacing-vertical $table-cell-spacing-horizontal; /* 4 */
      margin-left: -$table-cell-spacing-horizontal;
      margin-right: -$table-cell-spacing-horizontal;
      line-height: $table-cell-size - ($table-cell-spacing-vertical * 2);
      text-decoration: none;
      @include transition;

      &:focus,
      &:hover {
        background: $table-header-background-hover;
      }
    }
  }


  /**
   * Body
   *
   * 1. Small extra info below title display:block to display below title
   */

  td {
    @include border(bottom, $table-cell-border);
    padding: $table-cell-spacing-vertical $table-cell-spacing-horizontal;
    background: $table-cell-background-even;
    line-height: 18px;
  }

  tr:last-child {
    td {
      border-bottom: none;
    }
  }


  tr:nth-child(odd) {
    td {
      background: $table-cell-background-odd;
    }
  }


  /* Hover background */
  tr,
  tr:nth-child(n) {
    &:focus,
    &:hover {
      td {
        background: $table-cell-background-even-hover;
      }
    }
  }
}




/**
 * Selected
 *
 * When rows are selected they are highlighted
 *
 * 1. Coloring Links and extra elements that don't color automatically with their parent
 */

/*
1. Faking a bottom border, this shadow will disappear when pagination is positioned to bottom
*/

.k-table-pagination {
  padding: $table-pagination-spacing;
  background: $table-pagination-background;
  @include border(top, #d2d2d2);
  @if $borders != false {
    box-shadow: 0 1px 0 $table-pagination-border; /* 1 */
  }


  /* Flexbox */

  @include flexbox {
      flex: 0 0 auto;
  }


  /**
   * Pagination contents
   *
   * 1. Align vertically
   */

  .k-pagination__limit,
  .k-pagination__pages {
    display: inline-block; /* 1 */
    vertical-align: middle; /* 1 */
  }


  /* Pagination */

  .k-pagination__pages {
    padding-left: 0;
    margin: 0;
    border-radius: $border-radius-base;

    & > li {
      display: inline; // Remove list-style and block-level defaults

      & > a,
      & > span {
        position: relative;
        float: left; // Collapse white-space
        padding: $padding-base-vertical $padding-base-horizontal;
        line-height: $line-height-base;
        text-decoration: none;
        color: $pagination-color;
        background-color: $pagination-bg;
        border: 1px solid $pagination-border;
        margin-left: -1px;
      }

      &:first-child {
        & > a,
        & > span {
          margin-left: 0;
          @include border-left-radius($border-radius-base);
        }
      }

      &:last-child {
        & > a,
        & > span {
          @include border-right-radius($border-radius-base);
        }
      }
    }

    & > li > a,
    & > li > span {
      &:hover,
      &:focus {
        z-index: 2;
        color: $pagination-hover-color;
        background-color: $pagination-hover-bg;
        border-color: $pagination-hover-border;
      }
    }

    & > .k-is-active > a,
    & > .k-is-active > span {
      &,
      &:hover,
      &:focus {
        z-index: 3;
        color: $pagination-active-color;
        background-color: $pagination-active-bg;
        border-color: $pagination-active-border;
        cursor: default;
      }
    }
  }


  /* Limit */

  select {
    height: $table-pagination-height;
    width: auto;
    margin-right: $table-pagination-spacing;

    @include rtl {
      margin-right: auto;
      margin-left: $table-pagination-spacing;
    }
  }
}

.k-table-container {
  small {
    display: block;
    font-size: 11px;
    line-height: 11px;
  }

  a {
    text-decoration: none;

    &:hover,
    &:focus {
      text-decoration: underline;
    }
  }

  a {
    @include transition;
  }

  [class^="k-icon"] {
    vertical-align: baseline;
    font-size: 16px;
    line-height: 23px;
  }
}

.k-table-container {
  .k-table-data--right {
    text-align: right;

    @include rtl {
      text-align: left;
    }
  }

  .k-table-data--center {
    text-align: center;
  }

  .k-table-data--nowrap {
    white-space: nowrap;
  }

  .k-table-data--wrap {
    word-break: break-all;
  }

  .k-table-data--toggle {
    white-space: nowrap;
    padding-left: 0;
    padding-right: 0;
  }

  .k-table-data--ellipsis {
    max-width: 0;
    overflow: hidden;

    a, span, input, svg, div, small {
      display: block;
      vertical-align: middle;
      max-width: 100%;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }
  }
}

.k-table-container {
  .k-table__item--state {
    @include transition;

    // pointers for clickable elements
    button#{&},
    a#{&} {
      cursor: pointer;

      &:focus,
      &:hover {
        text-decoration: underline;
      }
    }

    &-pending {
      color: $color-warning;
    }

    &-expired {
      color: $color-neutral;
    }

    &-unpublished {
      color: $color-error;
    }

    &-published {
      color: $color-success;
    }
  }
}

.k-table-container {
  .k-table__sub-header {
    th {
      padding: .7em $table-cell-spacing-horizontal .5em;
      height: auto;
      font-size: .875em;
      line-height: 1.2em;
      vertical-align: middle;
      text-transform: uppercase;
      white-space: nowrap;
      background: $table-header-background;
      border-bottom-color: $table-cell-border;
    }
  }
}

.k-table-container {
  .k-table__item-level {
    white-space: nowrap;

    &:before {
      content: " ";
      display: block;
      width: 6px;
      height: 6px;
      border-left: 1px solid #bbb;
      border-bottom: 1px solid #bbb;
      position: absolute;
      top: 50%;
      margin-top: -.25em;

      @include rtl {
        border-left: none;
        border-right: 1px solid #bbb;
      }
    }
  }

  /* Set proper spacing before levels */

  @for $i from 1 through 15 {
    .k-table__item-level#{$i} {
      padding-left: 24px * ($i - 1) + $table-cell-spacing-horizontal;

      @include rtl {
        padding-left: 0;
        padding-right: 24px * ($i - 1) + $table-cell-spacing-horizontal;
      }

      &:before {
        left: (24px * ($i - 1)) - (18px - $table-cell-spacing-horizontal);

        @include rtl {
          left: auto;
          right: (24px * ($i - 1)) - (18px - $table-cell-spacing-horizontal);
        }
      }
    }
  }

  /* Do not display the level icon for the first level items */

  .k-table__item-level1:before {
    display: none;
  }
}

.k-table-container {
  tr.k-is-selected {
    td {
      background: $table-cell-accent-background;
      color: $table-cell-accent-color;

      a, i, span {
        color: $table-cell-accent-color; /* 1 */
      }
    }


    /* Re-set background on td's while hovering th */

    &:hover,
    &:focus {
      td {
        background-color: $table-cell-accent-background-hover;
      }
    }
  }
}


/**
 * Table elements
 */

.k-positioner {
  $k-positioner-width: 16px;
  $k-positioner-color: #ccc;

  display: inline-block;
  vertical-align: middle;
  width: $k-positioner-width;
  height: 4px;
  border-top: 1px solid $k-positioner-color;
  border-bottom: 1px solid $k-positioner-color;
  cursor: no-drop;

  &.k-is-active {
    cursor: move;
  }

  @include psuedo(before) {
    width: $k-positioner-width;
    height: 10px;
    border-top: 1px solid $k-positioner-color;
    border-bottom: 1px solid $k-positioner-color;
    top: -4px;
  }

  &:after {
    position: absolute;
    width: ($k-positioner-width * 2);
    height: ($k-positioner-width * 2);
    top: -($k-positioner-width - 1px);
    left: -($k-positioner-width / 2);
    content: attr(data-position);
    font-size: 10px;
    line-height: ($k-positioner-width * 2.75);
    text-indent: 3px;
    vertical-align: bottom;
    color: #333;
    text-shadow: 0 0 3px white;
    @include transition;
  }
}

.k-table-container {
  tr.k-is-selected {
    .k-positioner {
      &:after {
        color: white;
        text-shadow: none;
      }
    }
  }
}

th.k-sort-asc,
th.k-sort-desc {
  font-weight: $bold;

  a {
    cursor: pointer;

    &:focus,
    &:hover {
      text-decoration: none;
    }
  }
}

th.k-sort-asc {
  a {
    @include icon-after(sort-ascending, true) {
      margin-left: .5em;
      vertical-align: middle;
    }
  }
}

th.k-sort-desc {
  a {
    @include icon-after(sort-descending, true) {
      margin-left: .5em;
      vertical-align: middle;
    }
  }
}

.footable.breakpoint {
  .footable-toggle {
    display: inline-block;
    width: $footable-toggle-size;
    height: $footable-toggle-size;
    line-height: ($footable-toggle-size - ($footable-toggle-border-size * 2));
    border: $footable-toggle-border-size solid $footable-toggle-color;
    border-radius: 50%;
    margin-right: $table-cell-spacing-horizontal;
    text-align: center;

    @include rtl {
      margin-right: 0;
      margin-left: $table-cell-spacing-horizontal;
    }

    @include icon-after(chevron-bottom, true) {
      display: inline-block;
      width: $footable-toggle-icon-size;
      height: $footable-toggle-icon-size;
      margin: ((($footable-toggle-size - $footable-toggle-icon-size) / 2) - $footable-toggle-border-size);
      color: $footable-toggle-color;
      font-size: $footable-toggle-icon-size;
      line-height: 1.3em;
      transform: rotate(-90deg);
      @include transition;
    }
  }

  tr.k-is-selected {
    .footable-toggle {
      border-color: $footable-toggle-color-active;

      &:after {
        color: $footable-toggle-color-active;
      }
    }
  }

  tr.footable-detail-show {
    .footable-toggle {
      &:after {
        transform: rotate(0deg);
      }
    }
  }

  .footable-row-detail-row {
    margin-bottom: $table-cell-spacing-vertical;

    &:last-child {
      margin-bottom: 0;
    }
  }

  .footable-row-detail-name {
    font-weight: $bold;
  }
}

/**
 * This class is created by the floatThead plugin that's why it's not k-float-thead-wrapper
 *
 * 1. Overriding inline styling, we don't need a z-index of 1000+
 */

.floatThead-wrapper {
  z-index: 1 !important;

  @include flexbox {
    display: flex;
    flex: 0 1 auto;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }
}

.k-tabs {
  margin-bottom: 0;
  padding-left: 0;
  list-style: none;
  border-bottom: 1px solid $nav-tabs-border-color;
  @include clearfix;


  /* 1. Make the list-items overlay the bottom border */

  & > li {
    position: relative;
    display: block;
    float: left;
    margin-bottom: -1px; /* 1 */

    & > a {
      position: relative;
      display: block;
      padding: $nav-link-padding;
      margin-right: 2px;
      line-height: $line-height-base;
      border: 1px solid transparent;
      border-radius: $border-radius-base $border-radius-base 0 0;
      text-decoration: none;

      &:hover,
      &:focus {
        text-decoration: none;
        background-color: $nav-link-hover-bg;
        border-color: $nav-tabs-link-hover-border-color $nav-tabs-link-hover-border-color $nav-tabs-border-color;
      }
    }

    // Disabled state sets text to gray and nukes hover/tab effects
    &.k-is-disabled > a {
      color: $nav-disabled-link-color;

      &:hover,
      &:focus {
        color: $nav-disabled-link-hover-color;
        text-decoration: none;
        background-color: transparent;
        cursor: $cursor-disabled;
      }
    }

    // Active state, and its :hover to override normal :hover
    &.k-is-active > a {
      &,
      &:hover,
      &:focus {
        color: $nav-tabs-active-link-hover-color;
        background-color: $nav-tabs-active-link-hover-bg;
        border: 1px solid $nav-tabs-active-link-hover-border-color;
        border-bottom-color: transparent;
        cursor: default;
      }
    }
  }

  // Open dropdowns
  .k-is-open > a {
    &,
    &:hover,
    &:focus {
      background-color: $nav-link-hover-bg;
      border-color: $link-color;
    }
  }
}

/**
 * Tabbable tabs
 */

/* Hide tabbable panes to start, show them when `.k-is-active` */

.k-tabs-content {
  & > .k-tab {
    display: none;
    padding: $spacing-small;
  }

  & > .k-is-active {
    display: block;
  }
}

/**
 * 1. Set the z-index higher than the menus
 * 2. Setting a background so container will fall nicely over the sidebar
 * 3. Set transition for off canvas sidebar
 */

.k-title-bar {
  @include border(bottom, $titlebar-border);
  position: relative; /* 1 */
  z-index: 10; /* 1 */
  display: table;
  width: 100%;
  background: $titlebar-background; /* 2 */
  color: $titlebar-color;
  @include transition(transform); /* 3 */


  /* When flexbox is in use */

  @include flexbox {
    flex: 0 0 auto;
  }
}


/**
 * Titlebar when toggled
 *
 * 1. Set left to the width of the menu so the menu will appear
 * 2. Set right to the negative width of the menu so the menu will appear
 * 3. Reset when screen is wide enough
 */

.k-show-left-menu {
  & > .k-title-bar {
    transform: translate($sidebar-width, 0); /* 1 */

    @include rtl {
      transform: translate(-($sidebar-width), 0);
    }

    @include breakpoint($desktop) {
      transform: translate(0, 0); /* 3 */

      @include rtl {
        transform: translate(0, 0);
      }
    }
  }
}

.k-show-right-menu {
  & > .k-title-bar {
    transform: translate(-($sidebar-width), 0); /* 2 */

    @include rtl {
      transform: translate($sidebar-width, 0); /* 2 */
    }

    @include breakpoint($desktop) {
      transform: translate(0, 0); /* 3 */

      @include rtl {
        transform: translate(0, 0);
      }
    }
  }
}


/**
 * Fake Overlay when toggled
 *
 * 1. Set opacity
 * 2. Set z-index so it will become visible
 */

.k-show-left-menu,
.k-show-right-menu {
  .k-title-bar:before {
    opacity: .75; /* 1 */
  }
}

.k-is-opened-left,
.k-is-opened-right {
  .k-title-bar:before {
    z-index: 2; /* 2 */
  }
}


/* Titlebar contents */

.k-title-bar {
  .k-off-canvas-overlay {
    bottom: -1px; /* Also cover the bottom border */
  }

  .k-off-canvas-menu-toggle-holder,
  h2 {
    display: table-cell;
    vertical-align: middle;
  }

  .k-off-canvas-menu-toggle-holder {
    white-space: nowrap;
  }

  h2 {
    width: 99%;
    padding: $spacing-small;
    text-align: center;

    @include breakpoint($beta) {
      text-align: left;

      @include rtl {
        text-align: right;
      }
    }
  }
}

.k-title-bar--mobile {
  @include border(bottom, $mobile-title-border);
  background: $mobile-title-background;

  @include breakpoint($charlie) {
    display: none;
  }
}

.k-toolbar {
  @include border(bottom, $toolbar-border);
  background: $toolbar-background;
  min-height: $toolbar-height;


  /* Faking flexbox for older browsers */

  @include breakpoint($desktop) {
    width: 100%;
    display: table;
  }


  /* When flexbox is in use */

  @include flexbox {
    flex: 0 0 auto;
  }
}


/* Toolbar contents */

.k-toolbar {
  .k-button {
    margin: 10px 0 10px 10px;

    /**
     * Make buttons square
     *
     * Only make them square if media queries are supported
     * By default we just want them as buttons
     */

    @include breakpoint(0, range, $charlie) {
      overflow: hidden;
      width: 32px;
      height: 32px;
      padding: 7px 6px;
      text-align: center;
    }
  }

  @include rtl {
    .k-button {
      margin: 10px 10px 10px 0;
    }
  }

  /**
   * Position the icons
   *
   * Only position them if media queries are supported
   * By default we just want them aligned normally
   */

  [class^="k-icon"] {
    @include breakpoint(0, range, $charlie) {
      display: block;
      margin: 0;
      width: $font-size-large;
      height: $font-size-large;
      font-size: $font-size-large;
      line-height: $font-size-large;
    }

    display: inline-block;
    margin: 0 .25em 0 .1em;
  }
}


/* Text on the toolbar buttons */

.k-toolbar__text {
  @include breakpoint(0, range, $charlie) {
    position: relative;
    z-index: -1;
    visibility: hidden;
  }
}

// Base class
.k-tooltip {
  position: absolute;
  z-index: $zindex-tooltip;
  display: block;
  // Our parent element can be arbitrary since tooltips are by default inserted as a sibling of their target element.
  // So reset our font and text properties to avoid inheriting weird values.
  font-size: $font-size-small;

  opacity: 0;

  &.in     { opacity: $tooltip-opacity; }
  &.top    { margin-top:  -3px; padding: $tooltip-arrow-width 0; }
  &.right  { margin-left:  3px; padding: 0 $tooltip-arrow-width; }
  &.bottom { margin-top:   3px; padding: $tooltip-arrow-width 0; }
  &.left   { margin-left: -3px; padding: 0 $tooltip-arrow-width; }
}

// Wrapper for the tooltip content
.k-tooltip__inner {
  max-width: $tooltip-max-width;
  padding: 3px 8px;
  color: $tooltip-color;
  text-align: center;
  background-color: $tooltip-bg;
  border-radius: $border-radius-base;
}

// Arrows
.k-tooltip__arrow {
  position: absolute;
  width: 0;
  height: 0;
  border-color: transparent;
  border-style: solid;
}
// Note: Deprecated .top-left, .top-right, .bottom-left, and .bottom-right as of v3.3.1
.k-tooltip {
  &.top .k-tooltip__arrow {
    bottom: 0;
    left: 50%;
    margin-left: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width $tooltip-arrow-width 0;
    border-top-color: $tooltip-arrow-color;
  }
  &.top-left .k-tooltip__arrow {
    bottom: 0;
    right: $tooltip-arrow-width;
    margin-bottom: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width $tooltip-arrow-width 0;
    border-top-color: $tooltip-arrow-color;
  }
  &.top-right .k-tooltip__arrow {
    bottom: 0;
    left: $tooltip-arrow-width;
    margin-bottom: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width $tooltip-arrow-width 0;
    border-top-color: $tooltip-arrow-color;
  }
  &.right .k-tooltip__arrow {
    top: 50%;
    left: 0;
    margin-top: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width $tooltip-arrow-width $tooltip-arrow-width 0;
    border-right-color: $tooltip-arrow-color;
  }
  &.left .k-tooltip__arrow {
    top: 50%;
    right: 0;
    margin-top: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width 0 $tooltip-arrow-width $tooltip-arrow-width;
    border-left-color: $tooltip-arrow-color;
  }
  &.bottom .k-tooltip__arrow {
    top: 0;
    left: 50%;
    margin-left: -$tooltip-arrow-width;
    border-width: 0 $tooltip-arrow-width $tooltip-arrow-width;
    border-bottom-color: $tooltip-arrow-color;
  }
  &.bottom-left .k-tooltip__arrow {
    top: 0;
    right: $tooltip-arrow-width;
    margin-top: -$tooltip-arrow-width;
    border-width: 0 $tooltip-arrow-width $tooltip-arrow-width;
    border-bottom-color: $tooltip-arrow-color;
  }
  &.bottom-right .k-tooltip__arrow {
    top: 0;
    left: $tooltip-arrow-width;
    margin-top: -$tooltip-arrow-width;
    border-width: 0 $tooltip-arrow-width $tooltip-arrow-width;
    border-bottom-color: $tooltip-arrow-color;
  }
}

// @TODO: Styling like in docman with folders and correct spacers
// @TODO: document this and put it in the styleguide / refactor

.k-tree {
  @include clearfix;
  margin: $list-spacing 0;

  .jqtree-element {
    padding: 0 $spacing-tiny 0 $spacing;

    &:hover,
    &:focus {
      background: $jqtree-hover-background;
    }
  }

  .jqtree-selected {
    & > .jqtree-element {
      color: $color-accent;
    }
  }
}

.jqtree-tree {
  @include clearfix;

  /* Reset default ul li styling */
  &, ul {
    @include reset;
    @include clearfix;
    float: left;
    min-width: 100%;
  }

  @include rtl {
    &, ul {
      float: right;
    }
  }
}

.jqtree-folder {
  @include clearfix;
}

.jqtree-element {
  position: relative;
  display: block;
  width: 100%;
  font-size: 0;
  line-height: 0;
  padding: 0;
  white-space: nowrap;
  height: auto;
  cursor: pointer;

  a {
    padding: 0;
    background: transparent;
  }

  .jqtree-toggler {
    height: 0;
    padding-top: 30px;
    overflow: hidden;
    color: inherit;
  }
}

.jqtree-whitespace,
.jqtree-toggler,
.jqtree-icon,
.jqtree-title {
  display: inline-block;
  font-size: 12px;
  height: 30px;
  line-height: 30px;
  vertical-align: middle;
}

.jqtree-whitespace,
.jqtree-toggler,
.jqtree-icon {
  width: 18px;
  text-align: center;
}

.jqtree-title {
  padding: 0 0 0 5px;

  @include rtl {
    padding: 0 5px 0 0;
  }
}

.jqtree-toggler {
  @include psuedo(before) {
    top: 50%;
    margin-top: -2px;
    left: 4px;
    @include caret(bottom);
    @include transition;

    @include rtl {
      left: auto;
      right: 4px;
    }
  }

  &:hover,
  &:focus {
    &:before {
      border-top-color: black;
    }
  }

  &.jqtree-closed {
    &:before {
      transform: rotate(-90deg);

      @include rtl {
        transform: rotate(90deg);
      }
    }
  }
}

.jqtree-selected {
  & > .jqtree-element {
    font-weight: $bold;
  }
}

.jqtree-closed {
  ul {
    display: none;
  }
}

.k-well {
  padding: $spacing-small;
  margin-bottom: $spacing;
  background-color: $well-bg;
  border: 1px solid $well-border;
  border-radius: $radius;
  box-shadow: inset 0 1px 1px rgba(0,0,0,.05);

  &:last-child {
    margin-bottom: 0;
  }

  p {
    margin-bottom: $spacing-small;

    &:last-child {
      margin-bottom: 0;
    }
  }
}

.k-well__content {
  padding: $spacing-small;
  background: white;
  border: 1px solid $well-border;
  border-radius: $radius;
}

.k-well {
  .k-tabs-content {
    margin: 0;
    background: white;
    border: 1px solid #ddd;
    border-top: none;
    border-radius: 0 0 $radius $radius;
  }
}

@import "component";
@import "content";
@import "content-wrapper";
@import "grid";
@import "flex-wrapper";
@import "koowa-container";
@import "sidebar";
@import "wrapper";

/**
 * 1. By using a max-width we essentially set a min-width when the contents are
 *    to large for the koowa container. The reason we don't use min-width is
 *    because if the content is only 300px we don't need horizontal scrollbars
 * 2. Reset the max-width when viewport is as large as minwidth
 * 3. Default font settings
 * 4. Default styling
 */

&.koowa-container {
  max-width: $minwidth; /* 1 */
  margin: 0; /* 6 */
  border: none; /* 6 */
  width: 100%;
  overflow: hidden;
  padding: $koowa-padding;

  font-size: $font-size; /* 3 */
  line-height: $line-height; /* 3 */
  background: $koowa-background; /* 4 */
  color: $color-type; /* 4 */

  -webkit-font-smoothing: $webkit-font-smoothing;
  -moz-osx-font-smoothing: $firefox-font-smoothing;
  -webkit-overflow-scrolling: touch;

  @include breakpoint($minwidth) {
    max-width: none; /* 2 */
  }


  /* When flexbox is enabled */

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
  }


  /* RTL */

  @include rtl {
    direction: rtl;
  }
}

/* Faking flexbox on older browsers by using table styling */

.k-wrapper {
  position: relative;

  @include breakpoint($desktop) {
    display: table;
    width: 100%;
  }


  /* When flexbox is in use */

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    overflow: hidden;
  }
}


/**
 * Wrapper when toggled
 *
 * 1. Hide overflowing when menu is toggled
 * 2. Set back to auto when screen is wide enough
 */

.k-is-opened-left,
.k-is-opened-right {
  overflow: hidden; /* 1 */

  @include breakpoint($desktop) {
    overflow: auto; /* 2 */
  }

  @include resizing {
    overflow: hidden;
  }
}

/**
 * 1. Clearing toolbar floats when flexbox is not in use
 */

.k-content-wrapper {
  clear: both; /* 1 */
  margin: 0;
  overflow: hidden;
  @include border(bottom, $sidebar-border);


  /* Faking flexbox on older browsers by using table styling */

  @include breakpoint($desktop) {
    display: table;
    width: 100%;
  }


  /* When flexbox is in use */

  @include flexbox {
    display: flex;
    flex-direction: row;
    flex: 1 1 auto;
    border-bottom: none;
  }
}

/**
 * The left and right sidebar
 */

.k-sidebar-left,
.k-sidebar-right {
  position: relative;
  overflow: auto;
  -webkit-overflow-scrolling: touch;


  @include resizing {
    overflow: hidden;
  }

  /* Faking flexbox on older browsers by using table styling */

  @include breakpoint($desktop) {
    @include allwidth($sidebar-width-small);
    display: table-cell;
    vertical-align: top;
  }


  /* Larger sidebar when space is available */

  @include breakpoint($delta) {
    @include allwidth($sidebar-width);
  }


  /* When JavaScript is enabled */

  @include js-enabled {
    @include allwidth($sidebar-width);
    position: absolute;
    z-index: 1;
    top: 0;
    height: 100%;
    display: none;


    /* On 'desktop' we 'open' the sidebar by default */

    @include breakpoint($desktop) {
      @include allwidth($sidebar-width-small);
      position: relative;
      height: auto;
      display: table-cell;
    }


    /* And we make it even larger when there's enough space */

    @include breakpoint($delta) {
      @include allwidth($sidebar-width);
    }

  }


  /* When flexbox is in use */

  @include flexbox($horizontal: $desktop) {
    display: flex;
    flex-direction: column;
    flex: 0 0 auto;
  }
}


/* Left sidebar specific styling */

.k-sidebar-left {
  @include border(right, $sidebar-border);
  background: $sidebar-background;
  color: $sidebar-color;

  @include rtl {
    border-right: none;
    @include border(left, $sidebar-border);
  }

  @include js-enabled {
    left: 0;
  }

  @include rtl {
    @include js-enabled {
      left: auto;
      right: 0;
    }
  }
}


/* When sidebar is toggled */

.k-is-opened-left {
  .k-sidebar-left {
    display: block;
  }
}


/* Right sidebar specific styling */

.k-sidebar-right {
  @include border(left, $sidebar-border);
  background: $sidebar-background;
  color: $sidebar-color;

  @include rtl {
    border-left: none;
    @include border(right, $sidebar-border);
  }

  @include js-enabled {
    right: 0;
  }

  @include rtl {
    @include js-enabled {
      right: auto;
      left: 0;
    }
  }
}


/* When sidebar is toggled */

.k-is-opened-right {
  .k-sidebar-right {
    display: block;
  }
}

/**
 * 1. Set the z-index higher than the menus
 * 2. Setting a background so container will fall nicely over the sidebar
 * 3. Set transition for off canvas sidebar
 */

.k-content {
  position: relative; /* 1 */
  z-index: 2; /* 1 */
  background: $content-background; /* 2 */
  @include transition(transform); /* 3 */


  /* Faking flexbox on older browsers by using table styling */

  @include breakpoint($desktop) {
    display: table-cell;
    width: 100%;
  }


  /**
   * When flexbox is in use
   *
   * 1. Reset width needed for table
   * 2. Set overflow for breadcrumbs to properly work
   */

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
    width: auto; /* 1 */
    overflow: hidden; /* 2 */
  }
}


/**
 * Container when toggled
 *
 * 1. Set left to the width of the menu so the menu will appear
 * 2. Set right to the negative width of the menu so the menu will appear
 * 3. Reset when screen is wide enough
 */

.k-show-left-menu {
  .k-content {
    transform: translate($sidebar-width, 0); /* 1 */

    @include breakpoint($desktop) {
      transform: translate(0, 0); /* 3 */
    }
  }

  @include rtl {
    .k-content {
      transform: translate(-($sidebar-width), 0);

      @include breakpoint($desktop) {
        transform: translate(0, 0);
      }
    }
  }
}

.k-show-right-menu {
  .k-content {
    transform: translate(-($sidebar-width), 0); /* 2 */

    @include breakpoint($desktop) {
      transform: translate(0, 0); /* 3 */
    }
  }

  @include rtl {
    .k-content {
      transform: translate($sidebar-width, 0);

      @include breakpoint($desktop) {
        transform: translate(0, 0);
      }
    }
  }
}

.k-is-opened-left,
.k-is-opened-right {
  .k-content {
    overflow: hidden;

    @include breakpoint($desktop) {
      overflow: auto; /* 3 */
    }

    @include resizing {
      overflow: hidden;
    }
  }
}


/**
 * The overlay
 *
 * 1. Cover the entire container
 * 2. Hide by setting z-index to -1 because item can't transition if display is set from none to block
 * 3. Set opacity to 0 by default
 * 4. Hide overlay when screen is wide enough
 */

.k-off-canvas-overlay {
  @include coverall; /* 1 */
  z-index: -1; /* 2 */
  display: block;
  opacity: 0; /* 3 */
  content: " ";
  background: $overlay-background;
  @include transition(opacity);

  @include breakpoint($desktop) {
    display: none; /* 4 */
  }
}


/**
 * Overlay when toggled
 *
 * 1. Set opacity
 * 2. Set z-index to 1 so it will become visible
 */

.k-show-left-menu,
.k-show-right-menu {
  .k-off-canvas-overlay {
    opacity: $overlay-opacity; /* 1 */
  }
}

.k-is-opened-left,
.k-is-opened-right {
  .k-off-canvas-overlay {
    z-index: 9; /* 2 */
  }
}

.k-component {
  background: $component-background;

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }
}

/* Container */

.k-container {
  @include clearfix;
  overflow: hidden;
  width: 100%;
  margin: 0 auto (-($spacing));
  padding: $spacing ($spacing / 2);

  /* When flexbox is in use */

  @include flexbox {
    flex: 0 0 auto;
  }

  @include resizing {
    overflow: hidden;
  }
}


/* --------------------------------------------------------------------------
   Grid children
   -------------------------------------------------------------------------- */


/**
 * Main container
 */

.k-container__main {
  margin-bottom: $spacing;

  @include breakpoint($charlie) {
    float: left;
    margin: 0 ($spacing / 2) $spacing;
    width: calc(100% * 8/12 - #{$spacing});
  }
}

/* When a sidebar is active next to it */

.k-sidebar + .k-content .k-container__main {
  @include breakpoint($charlie) {
    width: 100%;
  }

  @include breakpoint($delta) {
    width: calc(100% * 8/12 - #{$spacing});
    margin-bottom: 0;
  }
}


/**
 * Sub container
 */

.k-container__sub {
  margin-bottom: $spacing;

  @include breakpoint($charlie) {
    float: left;
    margin: 0 ($spacing / 2) $spacing;
    width: calc(100% * 4/12 - #{$spacing});
  }
}

/* When a sidebar is active next to it */

.k-sidebar + .k-content .k-container__sub {
  @include breakpoint($charlie) {
    width: 100%;
  }

  @include breakpoint($delta) {
    width: calc(100% * 4/12 - #{$spacing});
    margin-bottom: 0;
  }
}


/**
 * Full container
 */

.k-container__full {
  clear: both;
  width: auto;
  margin: 0 ($spacing / 2) $spacing;
}

.k-flex-wrapper {
  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }
}

section.css:
@import "a";
@import "dl";
@import "fieldset";
@import "img";
@import "input";
@import "label";
@import "legend";
@import "p";

a {
  color: $color-type;

  &:focus,
  &:hover {
    color: $color-type;
  }
}

/**
 * 1. Default bottom-margin of 1.5 times the font-size
 * 2. Using single colon so it works in IE8 as well
 * 3. No bottom-margin for all the last-childs so parents can have bottom padding
 */

dl {
  @include clearfix;
  margin-bottom: 1.5em; /* 1 */

  &:last-child { /* 2 */
    margin-bottom: 0; /* 3 */
  }
}

dt {
  @include reset;
  font-weight: $bold;
  float: left;
  clear: left;
  margin-right: $spacing-tiny;
}

dd {
  @include reset;
  display: block;
  margin-bottom: $spacing-tiny;

  &:last-child {
    margin-bottom: 0; /* 3 */
  }
}

/**
 * 1. Chrome and Firefox set a `min-width: min-content;` on fieldsets,
 *    so we reset that to ensure it behaves more like a standard block element.
 *    See https://github.com/twbs/bootstrap/issues/12359.
 * 2. Default bottom-margin of 1.5 times the font-size
 * 3. Using single colon so it works in IE8 as well
 * 4. No bottom-margin for all the last-childs so parents can have bottom padding
 */

fieldset {
  padding: 0;
  margin: 0;
  border: 0;
  min-width: 0; /* 1 */
  margin-bottom: 1.5em; /* 2 */

  &:last-child { /* 3 */
    margin-bottom: 0; /* 4 */
  }
}

/**
 * 1. Responsive images
 * 2. Eliminating line-height bug when used alone in container
 */

img {
  max-width: 100%; /* 1 */
  height: auto;

  &:only-child {
    vertical-align: middle; /* 2 */
  }
}

/**
 * 1. Force IE8 to wrap long content
 */

label {
  display: inline-block;
  vertical-align: middle;
  max-width: 100%; /* 1 */
  margin-bottom: 5px;
  font-weight: bold;

  &:last-child {
    margin-bottom: 0;
  }
}

legend {
  width: 100%;
  padding: 0;
  color: $legend-color;
  border: 0;
  display: block;
  margin-top: 0;
  margin-bottom: $spacing;
  border: none;
  border-bottom: 1px solid darker($component-background);
  padding-top: $spacing;
  padding-bottom: 4px;
  font-size: $font-size--large;
  line-height: $line-height--large;
  font-weight: $regular;

  /* No margin top for the first legend */
  &:first-child {
    margin-top: 0;
  }

  /* No margin bottom for the last legend */
  &:last-child {
    margin-bottom: 0;
  }
}

/**
 * 1. Remove default top-margin
 * 2. Default bottom-margin of 1.5 times the font-size
 * 3. Using single colon so it works in IE8 as well
 * 4. No bottom-margin for all the last-childs so parents can have bottom padding
 */

p {
  margin-top: 0; /* 1 */
  margin-bottom: 1.5em; /* 2 */

  &:last-child { /* 3 */
    margin-bottom: 0; /* 4 */
  }
}

@import "alerts";
@import "breadcrumb";
@import "buttons";
@import "card";
@import "empty-state";
@import "form-block";
@import "forms";
@import "gallery";
@import "heading";
@import "icons";
@import "list";
@import "masthead";
@import "modal";
@import "navigation";
@import "optionlist";
@import "scopebar";
@import "sidebar-item";
@import "table";
@import "tabs";
@import "title-bar";
@import "toolbar";
@import "tooltip";
@import "tree";
@import "wells";

.k-alert {
  padding: $alert-padding;
  margin-bottom: $line-height-computed;

  &:last-child {
    margin-bottom: 0;
  }
}

/* Alternate styles */

.k-alert--success {
  @include alert-variant($alert-success-bg, $alert-success-text);
}

.k-alert--info {
  @include alert-variant($alert-info-bg, $alert-info-text);
}

.k-alert--warning {
  @include alert-variant($alert-warning-bg, $alert-warning-text);
}

.k-alert--danger {
  @include alert-variant($alert-danger-bg, $alert-danger-text);
}

/* Parent element
   ========================================================================== */

/**
 * 1. Scroll when items don't fit parent anymore
 */

.k-breadcrumb {
  @include border(bottom, $breadcrumb-border);
  overflow: auto; /* 1 */
  background: $breadcrumb-background;
  color: $breadcrumb-color;
  min-width: 100%;

  @include resizing {
    overflow: hidden;
  }
}


/* When flexbox is in use
   ========================================================================== */

@include flexbox {
  .k-breadcrumb {
    flex: 0 0 auto;
  }
}


/* Breadcrumb contents
   ========================================================================== */

.k-breadcrumb {

  /**
   * Links
   *
   * 1. Full background hover
   */

  a {
    color: $breadcrumb-color;
    cursor: pointer;

    &:focus,
    &:hover {
      background: $breadcrumb-background-hover; /* 1 */
    }
  }


  /**
   * List
   *
   * 1. Reset unordered list styling
   */

  ul {
    @include reset; /* 1 */


    /* Flexing items to get overflow ellipsis */

    @include flexbox {
      display: flex;
    }

  }


  /**
   * Styling list items
   *
   * 1. Set z-index to 0 so the :after psuedo element will be on top
   * 2. Make room for the psuedo element separator
   * 3. Make each item at least one breadcrumb size wide
   * 4. Hide by default so we can show only a few on mobile
   * 5. Hide overflowing content
   * 6. Proper spacing for non-flexbox use
   */

  li {
    position: relative; /* 1 */
    z-index: 0; /* 1 */
    padding-left: $breadcrumb-divider-size; /* 2 */
    min-width: $breadcrumb-size; /* 3 */
    display: none; /* 4 */
    overflow: hidden; /* 5 */
    vertical-align: middle; /* 6 */

    @include rtl {
      padding-left: 0;
      padding-right: $breadcrumb-divider-size;
    }


    /* Display on larger screens */

    @include breakpoint($breadcrumb-query) {
      display: inline-block;
    }


    /* Flexing items to get overflow ellipsis */

    @include flexbox($horizontal:$breadcrumb-query) {
      display: block;
      flex: 0 1 auto;
    }


    /* List item separator */

    @include psuedo(before, $content: false) {
      top: 0;
      bottom: 0;
      left: 0;
      width: $breadcrumb-divider-size;
      padding-top: $breadcrumb-size / 4;
      padding-bottom: $breadcrumb-size / 4;
      text-align: center;
      line-height: $breadcrumb-size / 2;
      content: $breadcrumb-divider-content;

      @include rtl {
        left: auto;
        right: 0;
      }
    }


    /* No separator on first item */

    &:first-child {
      padding-left: 0;

      @include rtl {
        padding-right: 0;
      }

      &:before {
        display: none;
      }
    }


    /**
     * Hover
     *
     * 1. Reset flexbox so the whole item is visible
     */

    &:hover {
      overflow: visible;

      @include flexbox {
        flex: 0 0 auto; /* 1 */
      }
    }
  }


  /**
   * Home and last elements
   *
   * 1. Always display these items
   * 2. No flexing for these items so the are readable
   */

  .k-breadcrumb__active,
  .k-breadcrumb__home {
    display: inline-block; /* 1 */
    overflow: visible; /* 1 */

    @include flexbox($horizontal:$breadcrumb-query) {
      flex: 0 0 auto; /* 2 */
    }
  }



  /**
   * Root element
   *
   * 1. Hide root text by default
   * 2. Show and position it when root is the active item to show more then just a home icon
   */

  .k-breadcrumb_root-text {
    @extend .visually-hidden; /* 1 */
  }

  .k-breadcrumb__home.k-breadcrumb__last {
    .k-breadcrumb_root-text {
      @extend .visually-shown; /* 2 */
      padding-left: $spacing-tiny; /* 2 */
      font-family: "Arial", sans-serif;

      @include rtl {
        padding-left: 0;
        padding-right: $spacing-tiny;
      }
    }
  }



  /**
   * One but last item
   *
   * Show this item on mobile to get a sense of where you are
   *
   * 1. Set 'missing' content
   * 2. Reset to default content on wider screens
   */

  li:nth-last-child(2) {
    display: inline-block;

    &:before {
      content: $breadcrumb-missing-content; /* 1 */

      @include breakpoint($breadcrumb-query) {
        content: $breadcrumb-divider-content; /* 2 */
      }
    }
  }


  /* Active item */

  .k-breadcrumb__last {
    .k-breadcrumb__item {
      font-weight: $bold;
    }
  }

}


/* Single breadcrumb items */

.k-breadcrumb__item {
  position: relative;
  z-index: 2;
  display: block;
  padding: round($breadcrumb-size / 4);
  min-width: $breadcrumb-size;
  min-height: $breadcrumb-size;
  overflow: hidden;
  text-align: center;
  line-height: floor($breadcrumb-size / 2);
  text-decoration: none;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.k-button {
  display: inline-block;
  margin-bottom: 0; // For input.k-button
  font-weight: $regular;
  text-align: center;
  vertical-align: middle;
  touch-action: manipulation;
  cursor: pointer;
  background-image: none; /* Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214 */
  border: 1px solid transparent;
  white-space: nowrap;
  box-shadow: none;
  text-shadow: none;
  padding: .35em .8em;
  font-size: $font-size;
  line-height: 21px;
  border-radius: $radius;
  user-select: none;
  margin-right: $spacing-tiny;

  &:last-child {
    margin-right: 0;
  }

  &,
  &:active,
  &.k-is-active {
    &:focus,
    &.focus {
      @include tab-focus;
    }
  }

  &:hover,
  &:focus {
    color: $btn-default-color;
    text-decoration: none;
  }

  &:active,
  &.k-is-active {
    outline: 0;
    background-image: none;
  }

  &[disabled],
  &.k-is-disabled {
    cursor: $cursor-disabled;
    opacity: .65;
  }
}

/**
 * 1. Remove underline when button is an `<a>` element
 * 2. Future-proof disabling of clicks on elements
 */

.k-button {
  &, &:hover, &:focus, &:active {
    text-decoration: none; /* 1 */
  }

  &.k-is-disabled {
    pointer-events: none; /* 2 */
  }
}


/* Alternate buttons */

.k-button--default {
  @include button-variant($btn-default-color, $btn-default-bg, $btn-default-border);
}

.k-button--primary {
  @include button-variant($btn-primary-color, $btn-primary-bg, $btn-primary-border);
}

.k-button--success {
  @include button-variant($btn-success-color, $btn-success-bg, $btn-success-border);
}

.k-button--info {
  @include button-variant($btn-info-color, $btn-info-bg, $btn-info-border);
}

.k-button--warning {
  @include button-variant($btn-warning-color, $btn-warning-bg, $btn-warning-border);
}

.k-button--danger {
  @include button-variant($btn-danger-color, $btn-danger-bg, $btn-danger-border);
}


/* Link buttons */
.k-button--link {
  color: $link-color;
  font-weight: normal;
  border-radius: 0;
  padding-left: 0;
  padding-right: 0;

  &,
  &:active,
  &.k-is-active,
  &[disabled],
  &.k-is-disabled {
    background-color: transparent;
    box-shadow: none;
  }

  &,
  &:hover,
  &:focus,
  &:active {
    border-color: transparent;
  }

  &:hover,
  &:focus {
    color: $link-hover-color;
    text-decoration: $link-hover-decoration;
    background-color: transparent;
  }

  &[disabled],
  &.k-is-disabled {
    &:hover,
    &:focus {
      color: $btn-link-disabled-color;
      text-decoration: none;
    }
  }
}


/* Block button */

.k-button--block {
  display: block;
  width: 100%;
}


/* Sizes */
.k-button--large {
  font-size: $font-size--medium;
  line-height: $line-height--medium;
}


.k-button--small {
  font-size: $font-size--small;
  line-height: $line-height--small;
}


.k-button--tiny {
  font-size: $font-size--tiny;
  line-height: $line-height--tiny;
}


/* Vertically space out multiple block buttons */

.k-button--block + .k-button--block {
  margin-top: 5px;
}


/* Specificity overrides */

input[type="submit"],
input[type="reset"],
input[type="button"] {
  &.k-button--block {
    width: 100%;
  }
}

.k-button-group {
  position: relative;
  display: inline-block;
  vertical-align: middle;

  & > .k-button {
    position: relative;
    float: left;
    margin: 0;

    /* Bring the "active" button to the front */
    &:hover,
    &:focus,
    &:active,
    &.k-is-active {
      z-index: 2;
    }
  }

  /* Prevent double borders when buttons are next to each other */
  .k-button + .k-button,
  .k-button + .k-button-group,
  .k-button-group + .k-button,
  .k-button-group + .k-button-group {
    margin-left: -1px;

    &:first-child {
      margin-left: 0;
    }
  }

  & > .k-button:not(:first-child):not(:last-child) {
    border-radius: 0;
  }

  & > .k-button:first-child:not(:last-child) {
    @include border-right-radius(0);
  }

  & > .k-button:last-child:not(:first-child) {
    @include border-left-radius(0);
  }

}

.k-card {
  width: 100%;
  margin-bottom: 1.5em;

  &:last-child {
    margin-bottom: 0;
  }
}

.k-card__body {
  @include transition;
  display: block;
  backface-visibility: visible;
  background: colorpicker(primary, 5);
  font-size: 14px;
  border: 1px solid #ddd;

  a#{&} {
    color: $color-accent; // This is the <a> so put color here
    cursor: pointer; // This is the <a> so put cursor here
  }
}

.k-card__body--preview {
  @include ratio-block(4 to 3);
}

.k-card__preview-content-wrapper {
  @include coverall(1px);
  backface-visibility: hidden;
  overflow: hidden;
}

.k-card__preview-content {
  width: 100%;
  height: 100%;
  max-height: 100%;
  overflow: hidden;
  vertical-align: middle;
  display: inline-block;
  text-align: center;
  font-size: 0;
  white-space: nowrap;
  padding: 1px; // space images with 1px

  &:before {
    content: ' ';
    display: inline-block;
    height: 100%;
    vertical-align: middle;
  }

  @include flexbox {
    display: flex;
    align-items: center;
    justify-content: center;
  }
}

.k-card__preview {
  display: inline-block;
  vertical-align: middle;
  text-align: center;
  max-height: 100%;
  max-width: 100%;
  overflow: hidden;
  font-size: $font-size;

  @include flexbox {
    flex: 0 0 auto;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  & > img,
  & > span {
    display: inline-block;
    max-height: 100%;
    vertical-align: middle;

    @include flexbox {
      flex: 0 1 auto;
    }
  }
}

.k-card__title {
  display: block;
  padding: 10px;
  width: 100%;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;

  label {
    font-weight: $regular;
    display: block;
    width: 100%;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    padding-left: 1px;
  }
}

.datepicker {
  font-family: $font-family;
  font-size: $font-size;
  line-height: $line-height;
  direction: ltr;

  &.datepicker-inline {
    width: 220px;
  }

  &.datepicker-rtl {
    direction: rtl;

    table tr td span {
      float: right;
    }
  }

  &.datepicker-dropdown {
    top: 0;
    left: 0;

    &:before {
      content: '';
      display: inline-block;
      border-left: 7px solid transparent;
      border-right: 7px solid transparent;
      border-bottom: 7px solid #ccc;
      border-top: 0;
      border-bottom-color: rgba(0, 0, 0, .2);
      position: absolute;
    }
    &:after {
      content: '';
      display: inline-block;
      border-left: 6px solid transparent;
      border-right: 6px solid transparent;
      border-bottom: 6px solid white;
      border-top: 0;
      position: absolute;
    }

    &.datepicker-orient-left:before {
      left: 6px;
    }

    &.datepicker-orient-left:after {
      left: 7px;
    }

    &.datepicker-orient-right:before {
      right: 6px;
    }

    &.datepicker-orient-right:after {
      right: 7px;
    }

    &.datepicker-orient-top:before {
      top: -7px;
    }

    &.datepicker-orient-top:after {
      top: -6px;
    }

    &.datepicker-orient-bottom:before {
      bottom: -7px;
      border-bottom: 0;
      border-top: 7px solid #999;
    }

    &.datepicker-orient-bottom:after {
      bottom: -6px;
      border-bottom: 0;
      border-top: 6px solid white;
    }
  }

  & > div {
    display: none;
  }

  &.days div.datepicker-days {
    display: block;
  }

  &.months div.datepicker-months {
    display: block;
  }

  &.years div.datepicker-years {
    display: block;
  }

  table {
    margin: 0;
    user-select: none;
  }

  td,
  th {
    text-align: center;
    width: 20px;
    height: 20px;
    border: none;
    border-radius: 4px;
  }

  // Inline display inside a table presents some problems with
  // border and background colors.
  .table-striped & table tr {
    td, th {
      background-color: transparent;
    }
  }

  table tr td {
    &.day:hover {
      background: $gray-lighter;
      cursor: pointer;
    }

    &.old,
    &.new {
      color: $gray-light;
    }

    &.disabled,
    &.disabled:hover {
      background: none;
      color: $gray-light;
      cursor: default;
    }

    &.today,
    &.today:hover,
    &.today.disabled,
    &.today.disabled:hover {
      $todayBackground: lighten(orange, 30%);
      background: $todayBackground;
      color: #000; // add last
    }

    &.today:hover:hover {
      // Thank bootstrap 2.0 for this selector...
      // TODO: Bump min BS to 2.1, use $textColor in buttonBackground above
      color: #000;
    }

    &.today.active:hover {
      color: #fff;
    }

    &.range,
    &.range:hover,
    &.range.disabled,
    &.range.disabled:hover {
      background: $gray-lighter;
      border-radius: 0;
    }

    &.range.today,
    &.range.today:hover,
    &.range.today.disabled,
    &.range.today.disabled:hover {
      $todayBackground: mix(orange, $gray-lighter, 50%);
      background: $todayBackground;
      border-radius: 0;
    }

    &.selected,
    &.selected:hover,
    &.selected.disabled,
    &.selected.disabled:hover {
      color: #fff;
      text-shadow: 0 -1px 0 rgba(0, 0, 0, .25);
      background: lighten($gray-light, 10);
    }

    &.active,
    &.active:hover,
    &.active.disabled,
    &.active.disabled:hover {
      color: #fff;
      text-shadow: 0 -1px 0 rgba(0, 0, 0, .25);
      background: $btn-primary-bg;
    }

    span {
      display: block;
      width: 23%;
      height: 54px;
      line-height: 54px;
      float: left;
      margin: 1%;
      cursor: pointer;
      border-radius: 4px;

      &:hover {
        background: $gray-lighter;
      }

      &.disabled,
      &.disabled:hover {
        background: none;
        color: $gray-light;
        cursor: default;
      }

      &.active,
      &.active:hover,
      &.active.disabled,
      &.active.disabled:hover {
        color: #fff;
        text-shadow: 0 -1px 0 rgba(0, 0, 0, .25);
        background: $btn-primary-bg;
      }

      &.old,
      &.new {
        color: $gray-light;
      }
    }
  }

  th.datepicker-switch {
    width: 145px;
  }

  thead tr:first-child th,
  tfoot tr th {
    cursor: pointer;
    &:hover {
      background: $gray-lighter;
    }
  }

  // Basic styling for calendar-week cells
  .cw {
    font-size: 10px;
    width: 12px;
    padding: 0 2px 0 5px;
    vertical-align: middle;
  }

  thead tr:first-child th.cw {
    cursor: default;
    background-color: transparent;
  }
}

.input-append,
.input-prepend {
  &.date {
    .add-on i {
      display: block;
      cursor: pointer;
      width: 16px;
      height: 16px;
    }
  }
}

.input-daterange {
  input {
    text-align: center;
  }

  input:first-child {
    border-radius: 3px 0 0 3px;
  }

  input:last-child {
    border-radius: 0 3px 3px 0;
  }

  .add-on {
    display: inline-block;
    width: auto;
    min-width: 16px;
    height: $line-height-base;
    padding: 4px 5px;
    font-weight: normal;
    line-height: $line-height-base;
    text-align: center;
    text-shadow: 0 1px 0 white;
    vertical-align: middle;
    background-color: $gray-lighter;
    border: 1px solid #ccc;
    margin-left: -5px;
    margin-right: -5px;
  }
}

/* Dropdown arrow/caret */

.k-caret {
  display: inline-block;
  width: 0;
  height: 0;
  margin-left: 2px;
  vertical-align: middle;
  border-top:   $caret-width-base dashed;
  border-right: $caret-width-base solid transparent;
  border-left:  $caret-width-base solid transparent;
}


/* The dropdown wrapper (div) */

.k-dropdown {
  position: relative;
}


/**
 * The dropdown menu (ul)
 *
 * 1. Hide by default, but block on "open" of the menu
 * 2. Ensures proper alignment if parent has it changed (e.g., modal footer)
 */

.k-dropdown__menu {
  position: absolute;
  top: 100%;
  left: 0;
  z-index: $zindex-dropdown;
  display: none; /* 1 */
  float: left;
  min-width: 160px;
  padding: 5px 0;
  margin: 2px 0 0;
  list-style: none;
  font-size: $font-size-base;
  text-align: left; /* 2 */
  background-color: $dropdown-bg;
  border: 1px solid $dropdown-border;
  border-radius: $border-radius-base;
  box-shadow: 0 6px 12px rgba(0,0,0,.175);
  background-clip: padding-box;


  /**
    * Links within the dropdown menu
    *
    * 1. prevent links from randomly breaking onto new lines
    */

  a {
    display: block;
    padding: 3px 20px;
    clear: both;
    font-weight: normal;
    line-height: $line-height-base;
    color: $dropdown-link-color;
    white-space: nowrap; /* 1 */
  }


  /* RTL */
  @include rtl {
    left: auto;
    right: 0;
  }
}



/* Dividers (basically an hr) within the dropdown */

.k-dropdown__divider {
  height: 1px;
  margin: (($line-height-computed / 2) - 1) 0;
  overflow: hidden;
  background-color: #e5e5e5;
}


/* Hover/Focus state */

.k-dropdown__menu a {
  &:hover,
  &:focus {
    text-decoration: none;
    color: $dropdown-link-hover-color;
    background-color: $dropdown-link-hover-bg;
  }
}


/* Active state */

.k-dropdown__menu .k-is-active a {
  &,
  &:hover,
  &:focus {
    color: $dropdown-link-active-color;
    text-decoration: none;
    outline: 0;
    background-color: $dropdown-link-active-bg;
  }
}

/**
 * Disabled state
 *
 * Gray out text and ensure the hover/focus state remains gray
 */

.k-dropdown__menu .k-is-disabled a {
  &,
  &:hover,
  &:focus {
    color: $dropdown-link-disabled-color;
  }

  /* Nuke hover/focus effects */
  &:hover,
  &:focus {
    text-decoration: none;
    background-color: transparent;
    background-image: none; // Remove CSS gradient
    cursor: $cursor-disabled;
  }
}


/* Open state for the dropdown */

.k-dropdown.k-is-open {

  /* Show the menu */
  & > .k-dropdown__menu {
    display: block;
  }

  /* Remove the outline when :focus is triggered */
  & > a {
    outline: 0;
  }
}


/* Backdrop to catch body clicks on mobile, etc. */

.k-dropdown-backdrop {
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  z-index: ($zindex-dropdown - 10);
}


/**
 * Dropup
 *
 * Allow for dropdowns to go bottom up (aka, dropup-menu)
 */

.k-dropdown--dropup {

  /* Reverse the caret */
  .k-caret {
    border-top: 0;
    border-bottom: $caret-width-base dashed;
    content: "";
  }

  /* Different positioning for bottom up menu */
  .k-dropdown__menu {
    top: auto;
    bottom: 100%;
    margin-bottom: 2px;
  }
}

.k-dropdown__menu--grid {
  width: 212px;
  position: absolute;
  padding: 5px;

  img {
    max-width: 16px;
    max-height: 16px;
  }

  li {
    float: left;
    width: 40px;
    height: 40px;
    margin: 0;
    padding: 0;
    display: block;

    @include rtl {
      float: right;
    }

    a {
      display: block;
      padding: 0;
      margin: 0;
      width: 40px;
      height: 40px;
      text-align: center;
      border-radius: 3px;
      float: left;

      [class^="k-icon-document-"],
      [class*=" k-icon-document-"] {
        display: block;
        vertical-align: middle;
        margin: ((40px - 16px)/2) auto;
      }
    }
  }

  .k-dropdown__block-item {
    min-width: 100%;
    clear: both;
    height: auto;
    margin: 5px 0 -5px;

    a {
      position: relative;
      height: auto;
      width: auto;
      float: none;
      border-top: 1px solid #e5e5e5;
      text-align: left;
      margin: 0 -5px;
      padding: 5px;
      border-radius: 0 0 $radius $radius;

      @include rtl {
        text-align: right;
      }
    }
  }
}

.k-empty-state {
  margin: 0;
  font-size: 1.5em;
  line-height: 1.5em;
  text-align: center;
  background: $no-items-background;
  color: $no-items-color;

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
    justify-content: center;
  }

  p {
    margin-bottom: 1em;

    &:last-child {
      margin-bottom: 0;
    }
  }
}

/* The block itself */

.k-form-block {
  @include border(all, $form-block-border);
  background: $form-block-background;
}


/* The header */

.k-form-block__header {
  @include border(bottom, $form-block-header-border);
  padding: $form-block-header-spacing;
  background: $form-block-header-background;
  font-weight: $bold;
}


/* The content */

.k-form-block__content {
  padding: $form-block-content-spacing;
}

.k-inline-form-group {
  display: inline-block;
  margin-right: 1em;

  &:last-child {
    margin-right: 0;
  }

  @include rtl {
    margin-right: 0;
    margin-left: 1em;

    &:last-child {
      margin-left: 0;
      margin-right: 1em;
    }
  }

  label {
    font-weight: $regular;
  }

  .k-input-group,
  .k-button-group {
    display: block;
  }
}

.k-form-row-group {
  background: #fff;
  border-radius: 5px;
  border: 1px solid #ccc;
  margin-bottom: $spacing;
}

.k-form-row {
  margin: 0;
  border-bottom: 1px solid #ccc;
  padding-bottom: 10px;

  @include breakpoint($beta) {
    display: table;
    width: 100%;
    padding-bottom: 0;

    @include rtl {
      direction: rtl;
    }

    &:last-child {
      border-bottom: none;
    }
  }

  .k-form-row__item {
    padding: 10px 10px 0 10px;
    border-right: 1px solid #ccc;

    @include rtl {
      border-right: none;
      border-left: 1px solid #ccc;
    }

    @include breakpoint($beta) {
      display: table-cell;
      vertical-align: middle;
      padding: 10px;
    }

    &:last-child {
      border-right: none;

      @include rtl {
        border-left: none;
      }
    }
  }

  .k-form-row__item--button,
  .k-form-row__item--label {
    width: 1%;
    white-space: nowrap;
  }

  .k-form-row__item--label {
    border-right: none;
    padding-right: 0;

    @include rtl {
      border-left: none;
      padding-right: 10px;
      padding-left: 0;
    }
  }

  .k-form-row__item--input {
    @include breakpoint($beta) {
      padding: 5px;
    }
  }

  .k-form-control {
    margin: 0;
  }

  .k-form-row__message {
    a {
      display: block;
      padding: 10px;

      &:focus,
      &:hover {
        background: #eee;
      }
    }
  }

  &:first-child {
    .k-form-row__message {
      a {
        border-radius: 5px 5px 0 0;
      }
    }
  }

  &:last-child {
    .k-form-row__message {
      a {
        border-radius: 0 0 5px 5px;
      }
    }
  }
}

/**
 * Shared styling between radio boxes and checkboxes
 */

@include modernizr('.k-appearance.k-checked') {
  input[type="checkbox"],
  input[type="radio"] {

    /**
     * 1. Resetting the default styling used by browsers. Don't do this for -moz-
          because the checkbox and radio box will look very ugly
     * 2. Positioning relative to position psuedo before element nicely
     * 3. Vertically positioning next to text
     * 4. Aligning vertically next to labels
     * 5. Indicate that element is clickable
     */

    -webkit-appearance: none; /* 1 */
    /* -moz-appearance: don't set this; /* 1 */
    position: relative; /* 2 */
    height: $form-option-size;
    width: $form-option-size;
    margin-top: -1px; /* 3 */
    margin-bottom: 2px; /* 3 */
    margin-left: 0;
    margin-right: .33em;
    border: $form-option-border-size solid $form-option-border;
    padding: 0;
    background: $form-option-background;
    display: inline-block;
    vertical-align: middle;
    cursor: pointer; /* 5 */
    box-shadow: $form-option-shadow;
    @include transition(background);

    /* Hover to indicate element is clickable */

    &:hover {
      background: #f1f1f1;
    }

    /* Set disabled border color a bit lighter so it won't appear to be larger */

    &:disabled {
      border-color: #bbb;
    }

    /**
     * 1. Position psuedo element absolute to have full control
     * 2. Position top-left, margin, width and height will take care of centering
     * 3. Initially hide the psuedo element
     * 4. Show the psuedo element when item is checked
     */

    &:before {
      position: absolute; /* 1 */
      top: 0; /* 2 */
      left: 0; /* 2 */
      visibility: hidden; /* 3 */
      transform: scale(0,0); /* 3 */
      text-align: center;
      vertical-align: middle;
      background: transparent;
      @include transition;
    }

    &:checked:before {
      visibility: visible; /* 4 */
      transform: scale(1,1); /* 4 */
    }

  }

  /* Put some space between the box and the label */
  label {

    /**
     * <input id="input1">
     * <label for="input1">Label 1</label>
     * <input id="input2">
     * <label for="input2">Label 2</label>
     */

    & + input[type="checkbox"],
    & + input[type="radio"] {
      margin-left: 1em;
    }

    /**
     * <label><input id="input1"> Label 1</label>
     * <label><input id="input2"> Label 2</label>
     */

    & + label {
      margin-left: 1em;
    }
  }
}


/* RTL */

@include modernizr-rtl('.k-appearance.k-checked') {
  input[type="checkbox"],
  input[type="radio"] {
    margin-left: .33em;
    margin-right: 0;

    &:before {
      left: auto;
      right: 0;
    }
  }

  label {

    & + input[type="checkbox"],
    & + input[type="radio"] {
      margin-left: auto;
      margin-right: 1em;
    }

    & + label {
      margin-left: auto;
      margin-right: 1em;
    }
  }
}



/* ==========================================================================
   CSS only styled checkbox
   ========================================================================== */

/**
 * 1. Remove rounded corners by default
 * 2. Display a check icon in the psuedo before element
 * 3. Height of the box minus the border size
 * 4. Height of the psuedo element minus a 1px padding/margin to move icon
      away from the side of the box
 * 5. Set the color of the check icon
 */

@include modernizr('.k-appearance.k-checked') {
  input[type="checkbox"] {
    border-radius: 0; /* 1 */

    @include icon-before(check, true) {
      /* 2 */
      width: $form-option-size - ($form-option-border-size * 2); /* 3 */
      height: $form-option-size - ($form-option-border-size * 2); /* 3 */
      line-height: $form-option-size - ($form-option-border-size * 2); /* 3 */
      font-size: $form-option-size - ($form-option-border-size * 4); /* 4 */
      color: $color-accent; /* 5 */
    }
  }
}



/* ==========================================================================
   CSS only styled radio box
   ========================================================================== */

/**
 * 1. Making the radio box round
 * 2. Adding content to display the psuedo element
 * 3. Small inner circle, scaling with the box itself
 * 4. positioning the inner circle
 * 5. Making inner circle actually round
 */

@include modernizr('.k-appearance.k-checked') {
  input[type="radio"] {
    border-radius: 50%; /* 1 */

    &:before {
      content: " "; /* 2 */
      width: $form-option-size - ($form-option-border-size * 8); /* 3 */
      height: $form-option-size - ($form-option-border-size * 8); /* 3 */
      margin: 3px; /* 4 */
      background: $form-option-color;
      border-radius: 50%; /* 5 */
    }
  }
}

/**
 * Common form controls
 * Shared size and type resets for form controls. Apply `.k-form-control` to any
 * of the following form controls:
 *
 * select
 * textarea
 * input[type="text"]
 * input[type="password"]
 * input[type="datetime"]
 * input[type="datetime-local"]
 * input[type="date"]
 * input[type="month"]
 * input[type="time"]
 * input[type="week"]
 * input[type="number"]
 * input[type="email"]
 * input[type="url"]
 * input[type="search"]
 * input[type="tel"]
 * input[type="color"]
 *
 * 1. Make inputs at least the height of their button counterpart (base line-height + padding + border)
 * 2. Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214
 * 3. Note: This has no effect on <select>s in some browsers, due to the limited stylability of <select>s in CSS.
 * 4. Customize the `:focus` state to imitate native WebKit styles.
 * 5. Unstyle the caret on `<select>`s in IE10+.
 * 6. iOS fix for unreadable disabled content; see https://github.com/twbs/bootstrap/issues/11655
 */

.k-form-control {
  display: block;
  width: 100%;
  height: $input-height-base; /* 1 */
  padding: $padding-base-vertical $padding-base-horizontal;
  font-size: 16px;
  line-height: $line-height-base;
  color: $input-color;
  background-color: $input-bg;
  background-image: none; /* 2 */
  border: 1px solid $input-border;
  border-radius: $input-border-radius; /* 3 */
  box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
  transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;


  /* 4 */

  @include form-control-focus;


  /* Placeholders */

  &::-moz-placeholder {
    color: $input-color-placeholder;
    opacity: 1;
  }

  &:-ms-input-placeholder {
    color: $input-color-placeholder;
  }

  &::-webkit-input-placeholder  {
    color: $input-color-placeholder;
  }

  /* 5 */
  &::-ms-expand {
    border: 0;
    background-color: transparent;
  }


  /* Disabled and read-only inputs */

  &[disabled],
  &[readonly],
  fieldset[disabled] & {
    background-color: $input-bg-disabled;
    opacity: 1; /* 6 */
  }

  &[disabled],
  fieldset[disabled] & {
    cursor: $cursor-disabled;
  }
}


/* Reset height for `textarea`s */

textarea.k-form-control {
  height: auto;
}


/**
 * Search inputs in iOS
 *
 * This overrides the extra rounded corners on search inputs in iOS so that our
 * `.k-form-control` class can properly style them. Note that this cannot simply
 * be added to `.k-form-control` as it's not specific enough. For details, see
 * https://github.com/twbs/bootstrap/issues/11586.
 */

input[type="search"] {
  -webkit-appearance: none;
}


/**
 * Special styles for iOS temporal inputs
 *
 * In Mobile Safari, setting `display: block` on temporal inputs causes the
 * text within the input to become vertically misaligned. As a workaround, we
 * set a pixel line-height that matches the given height of the input, but only
 * for Safari. See https://bugs.webkit.org/show_bug.cgi?id=139848
 *
 * Note that as of 8.3, iOS doesn't support `datetime` or `week`.
 */

@media screen and (-webkit-min-device-pixel-ratio: 0) {
  input[type="date"],
  input[type="time"],
  input[type="datetime-local"],
  input[type="month"] {
    &.k-form-control {
      line-height: $input-height-base;
    }

    .k-input-group--small & {
      line-height: $input-height-small;
    }

    .k-input-group--large & {
      line-height: $input-height-large;
    }
  }
}


/**
 * Form groups
 *
 * Designed to help with the organization and spacing of vertical forms. For
 * horizontal forms, use the predefined grid classes.
 */

.k-form-group {
  margin: 0 0 $spacing 0;

  &:last-child {
    margin-bottom: 0;
  }
}

/**
 * Static form control text
 *
 * Apply class to a `p` element to make any string of text align with labels in
 * a horizontal form layout.
 *
 * 1. Size it appropriately next to real form controls
 * 2. Remove default margin from `p`
 */

.k-static-form-label {
  display: inline-block;
  vertical-align: middle;
  max-width: 100%; /* 1 */
  margin-bottom: 5px;
  font-weight: bold;

  &:last-child {
    margin-bottom: 0;
  }
}


/**
 * Form control sizing
 *
 * Build on `.k-form-control` with modifier classes to decrease or increase the
 * height and font-size of form controls.
 *
 * The `.k-form-group-* k-form-control` variations are sadly duplicated to avoid the
 * issue documented in https://github.com/twbs/bootstrap/issues/15074.
 */

@include input-size('.input-sm', $input-height-small, $padding-small-vertical, $padding-small-horizontal, 16px, $line-height-small, $input-border-radius-small);
.k-form-group--small {
  .k-form-control {
    height: $input-height-small;
    padding: $padding-small-vertical $padding-small-horizontal;
    font-size: $font-size-small;
    line-height: $line-height-small;
    border-radius: $input-border-radius-small;
  }

  select.k-form-control {
    height: $input-height-small;
    line-height: $input-height-small;
  }

  textarea.k-form-control,
  select[multiple].k-form-control {
    height: auto;
  }
}

@include input-size('.input-lg', $input-height-large, $padding-large-vertical, $padding-large-horizontal, $font-size-large, $line-height-large, $input-border-radius-large);
.k-form-group--large {
  .k-form-control {
    height: $input-height-large;
    padding: $padding-large-vertical $padding-large-horizontal;
    font-size: $font-size-large;
    line-height: $line-height-large;
    border-radius: $input-border-radius-large;
  }

  select.k-form-control {
    height: $input-height-large;
    line-height: $input-height-large;
  }

  textarea.k-form-control,
  select[multiple].k-form-control {
    height: auto;
  }
}

.k-has-success {
  @include form-control-validation($state-success-text, $state-success-text, $state-success-bg);
}
.k-has-warning {
  @include form-control-validation($state-warning-text, $state-warning-text, $state-warning-bg);
}
.k-has-error {
  @include form-control-validation($state-danger-text, $state-danger-text, $state-danger-bg);
}

/**
 * 1. For dropdowns
 * 2. Prevent input groups from inheriting border styles from table cells when placed within a table
 * 3. Ensure that the input is always above the *appended* addon button for proper border colors.
 * 4. IE9 fubars the placeholder attribute in text inputs and the arrows on
      select elements in input groups. To fix it, we float the input.
 * 5. Put focused element on top of the rest
 */

.k-input-group {
  position: relative; /* 1 */
  display: table;
  border-collapse: separate; /* 2 */

  .k-form-control {

    /* 3 */
    position: relative;
    z-index: 2;

    /* 4 */
    float: left;

    width: 100%;
    margin-bottom: 0;

    &:focus {
      z-index: 3; /* 5 */
    }
  }
}


/* Sizes */

.k-input-group--large > .k-form-control,
.k-input-group--large > .k-input-group__addon,
.k-input-group--large > .k-input-group__button > .k-button {
  @extend .input-lg;
}
.k-input-group--small > .k-form-control,
.k-input-group--small > .k-input-group__addon,
.k-input-group--small > .k-input-group__button > .k-button {
  @extend .input-sm;
}

/* Input group parts */

.k-input-group__addon,
.k-input-group__button,
.k-input-group .k-form-control {
  display: table-cell;

  &:not(:first-child):not(:last-child) {
    border-radius: 0;
  }
}


/**
 * Addon and addon wrapper for buttons
 *
 * 1. Match the inputs
 */

.k-input-group__addon,
.k-input-group__button {
  width: 1%;
  white-space: nowrap;
  vertical-align: middle; /* 1 */
}


/* Text addon */

.k-input-group__addon {
  padding: $padding-base-vertical $padding-base-horizontal;
  font-size: $font-size-base;
  font-weight: normal;
  line-height: 1;
  color: $input-color;
  text-align: center;
  background-color: $input-group-addon-bg;
  border: 1px solid $input-group-addon-border-color;
  border-radius: $input-border-radius;


  /* Sizing */

  &.input-sm {
    padding: $padding-small-vertical $padding-small-horizontal;
    font-size: $font-size-small;
    border-radius: $input-border-radius-small;
  }

  &.input-lg {
    padding: $padding-large-vertical $padding-large-horizontal;
    font-size: $font-size-large;
    border-radius: $input-border-radius-large;
  }


  /* Nuke default margins from checkboxes and radios to vertically center within. */

  input[type="radio"],
  input[type="checkbox"] {
    margin-top: 0;
  }
}


/* Reset rounded corners */
.k-input-group .k-form-control:first-child,
.k-input-group__addon:first-child,
.k-input-group__button:first-child > .k-button,
.k-input-group__button:first-child > .k-button-group > .k-button,
.k-input-group__button:first-child > .k-dropdown__toggle,
.k-input-group__button:last-child > .k-button:not(:last-child):not(.k-dropdown__toggle),
.k-input-group__button:last-child > .k-button-group:not(:last-child) > .k-button {
  @include border-right-radius(0);
}

.k-input-group__addon:first-child {
  border-right: 0;
}

.k-input-group .k-form-control:last-child,
.k-input-group__addon:last-child,
.k-input-group__button:last-child > .k-button,
.k-input-group__button:last-child > .k-button-group > .k-button,
.k-input-group__button:last-child > .k-dropdown__toggle,
.k-input-group__button:first-child > .k-button:not(:first-child),
.k-input-group__button:first-child > .k-button-group:not(:first-child) > .k-button {
  @include border-left-radius(0);
}

.k-input-group__addon:last-child {
  border-left: 0;
}


/**
 * Button input groups
 *
 * 1. Jankily prevent input button groups from wrapping with `white-space` and
      `font-size` in combination with `inline-block` on buttons.
 * 2. Negative margin for spacing, position for bringing hovered/focused/actived
      element above the siblings.
 * 3. Bring the "active" button to the front
 * 4. Negative margin to only have a 1px border between the two
 */

.k-input-group__button {
  position: relative;

  /* 1 */
  font-size: 0;
  white-space: nowrap;

  /* 2 */
  > .k-button {
    position: relative;

    + .k-button {
      margin-left: -1px;
    }

    /* 3 */
    &:hover,
    &:focus,
    &:active {
      z-index: 2;
    }
  }

  /* 4 */
  &:first-child {
    > .k-button,
    > .k-button-group {
      margin-right: -1px;
    }
  }

  &:last-child {
    > .k-button,
    > .k-button-group {
      z-index: 2;
      margin-left: -1px;
    }
  }
}

.k-gallery-container {
  background: $grid-background;

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    overflow: auto;
  }
}

.k-gallery {
  background: $grid-background;
  padding: $grid-spacing / 2;
  overflow: auto;

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
  }
}

.k-gallery__items {
  @include clearfix;

  @include flexbox {
    flex: 1 1 auto;
  }
}

.k-gallery__item {
  float: left;
  padding: $grid-spacing / 2;
  width: 100%;

  @include rtl {
    float: right;
  }

  // Set breakpoints
  @include setGridBreakpoint;

  // Selected styling
  &.k-is-selected {
    .k-card__title {
      background: $color-accent;
      border-color: darkest($color-accent);
      color: $color-accent-type;

      a {
        color: $color-accent-type;
      }
    }

    .k-card__body {
      color: $color-accent-type;
      border-color: darker($color-accent);
    }

    &:hover,
    &:focus {
      .k-card__title {
        background: darkest($color-accent);
      }
    }
  }
}

/* Make sure that the files are separated from the folders */
.k-gallery__item--folder + .k-gallery__item--file {
  clear: left;
}

.k-heading {
  font-size: $font-size--large;
  line-height: $line-height--large;
  margin-bottom: $spacing;

  &:last-child {
    margin-bottom: 0;
  }
}

.k-icon--accent {
  color: $color-accent;
}

.k-icon--success {
  color: $color-success;
}

.k-icon--error {
  color: $color-error;
}

.k-icon--size-16 {
  font-size: 16px;
}

.k-icon--size-32 {
  font-size: 32px;
}

.k-icon--size-48 {
  font-size: 48px;
}

.k-icon--size-64 {
  font-size: 64px;
}

/**
 * 1. Reset default <ul> styling
 */

.k-list {
  @include reset($margin: false); /* 1 */
  margin: $list-spacing 0;


  /* Items */

  li {
    display: block;
    line-height: $line-height;
  }


  /**
   * Links
   *
   * 1. Make item fully clickable
   * 2. removing underlines
   */

  a {
    display: block; /* 1 */
    padding-top: $list-spacing;
    padding-bottom: $list-spacing;
    padding-right: $spacing;
    padding-left: $spacing;
    color: $list-color;
    text-decoration: none; /* 2 */


    /* Hover & focus state */

    &:hover,
    &:focus {
      background: $list-hover-background;
      color: $list-hover-color;
      text-decoration: none; /* 2 */
    }
  }


  /* Active link */

  .k-is-active {
    a {
      font-weight: $bold;
      background: $list-active-background;
      color: $list-active-color;

      &:hover {
        background: transparent;
        cursor: default;
      }
    }
  }


  /* Icons */

  [class^="k-icon"] {
    display: inline-block;
    margin-right: .75em;

    @include rtl {
      margin-right: auto;
      margin-left: .75em;
    }
  }
}

.k-masthead {
  @include border(bottom, $masthead-border);
  min-height: $masthead-height;
  background: $masthead-background;
  color: $masthead-color;
  padding: ($masthead-height - $font-size-large) / 2;
  line-height: $masthead-height - $font-size-large;
  font-size: $font-size-large;


  /* Faking flexbox for older browsers */

  @include breakpoint($desktop) {
    width: 100%;
    display: table;
  }


  /* When flexbox is in use */

  @include flexbox {
    flex: 0 0 auto;
  }
}

/* Magnific Popup CSS */
.mfp-bg {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1042;
  overflow: hidden;
  position: fixed;
  background: #0b0b0b;
  opacity: 0.8;

  @include rtl-outside-koowa {
    left: auto;
    right: 0;
  }
}

.mfp-wrap {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1043;
  position: fixed;
  -webkit-backface-visibility: hidden;

  @include rtl-outside-koowa {
    left: auto;
    right: 0;
  }

  // Importing the well here so the advanced permissions popup looks nice
  @import "wells";
}

.mfp-container {
  text-align: center;
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  padding: 0 8px;
  box-sizing: border-box;

  @include rtl-outside-koowa {
    left: auto;
    right: 0;
  }
}

.mfp-container:before {
  content: ' ';
  display: inline-block;
  height: 100%;
  vertical-align: middle;
}

.mfp-align-top .mfp-container:before {
  display: none;
}

.mfp-content {
  position: relative;
  display: inline-block;
  vertical-align: middle;
  margin: 0 auto;
  text-align: left;
  z-index: 1045;
  min-width: 300px;

  @include rtl-outside-koowa {
    text-align: right;
  }
}

.mfp-inline-holder .mfp-content,
.mfp-ajax-holder .mfp-content {
  width: auto;
  cursor: auto;
}

.mfp-ajax-cur {
  cursor: progress;
}

.mfp-zoom-out-cur,
.mfp-zoom-out-cur .mfp-image-holder .mfp-close {
  cursor: -moz-zoom-out;
  cursor: -webkit-zoom-out;
  cursor: zoom-out;
}

.mfp-zoom {
  cursor: pointer;
  cursor: -webkit-zoom-in;
  cursor: -moz-zoom-in;
  cursor: zoom-in;
}

.mfp-auto-cursor .mfp-content {
  cursor: auto;
}

.mfp-close,
.mfp-arrow,
.mfp-preloader,
.mfp-counter {
  user-select: none;
}

.mfp-loading.mfp-figure {
  display: none;
}

.mfp-hide {
  display: none !important;
}

.mfp-preloader {
  color: #cccccc;
  position: absolute;
  top: 50%;
  width: auto;
  text-align: center;
  margin-top: -0.8em;
  left: 8px;
  right: 8px;
  z-index: 1044;
}

.mfp-preloader a {
  color: #cccccc;
}

.mfp-preloader a:hover {
  color: white;
}

.mfp-s-ready .mfp-preloader {
  display: none;
}

.mfp-s-error .mfp-content {
  display: none;
}

button.mfp-close,
button.mfp-arrow {
  overflow: visible;
  cursor: pointer;
  background: transparent;
  border: 0;
  -webkit-appearance: none;
  display: block;
  padding: 0;
  z-index: 1046;
  -webkit-box-shadow: none;
  box-shadow: none;
}

button::-moz-focus-inner {
  padding: 0;
  border: 0;
}

.mfp-close {
  width: 44px;
  height: 44px;
  line-height: 44px;
  position: absolute;
  right: 0;
  top: 0;
  text-decoration: none;
  text-align: center;
