    border-left: 27px solid #3f3f3f;
    margin-left: 0;
    margin-right: 25px;
  }
}

.mfp-arrow-right {
  right: 0;

  @include rtl-outside-koowa {
    right: auto;
    left: 0;
  }
}

.mfp-arrow-right:after,
.mfp-arrow-right .mfp-a {
  border-left: 17px solid white;
