// Custom classes
// Namespace them inside bot wrap and container to win from all other mfp styles
// This makes sure modals in both frontend and backend look the same

.mfp-wrap {

  .mfp-container {

    min-height: 420px;

    &.mfp-inline-holder {
      padding-top: 40px;
      padding-bottom: 40px;

      .mfp-content {
        max-width: 1200px;
        margin: 0 auto;
      }
    }

    .mfp-inline {
      display: block;
      width: auto;
      height: auto;
      padding: 15px;
    }

    .mfp-close {
      margin: 0;
      padding: 0;
      color: white;
      text-align: center;
      width: 40px;
      height: 40px;
      line-height: 40px;
      top: -40px;
      font-size: 24px;
      right: -14px;

      @include rtl-outside-koowa {
        right: auto;
        left: -14px;
      }

      &:hover {
        border: none;
        background: transparent;
        margin: 0;
        padding: 0;
        color: white;
      }
    }

    &.mfp-image-holder {
      .mfp-close {
        top: 0;
      }
    }

    .mfp-iframe-scaler .mfp-iframe,
    .mfp-inline {
      background: white;
      box-shadow: 0 0 8px rgba(0, 0, 0, 0.6);
    }
  }
}

// Special classes for our file dialog popup
.koowa_dialog_modal {
  .mfp-content {
    max-height: 100%;
    min-height: 100%;
    height: 100%;
  }
  .mfp-content .mfp-iframe-scaler {
    padding: 0;
    margin: 0;
    height: 100%;

    .mfp-iframe {
      position: relative;
    }
  }

  @media screen and (min-width: 712px) {
    .mfp-content {
      min-height: 0;
      max-width: 1080px !important;
      max-height: 800px !important;
    }
    .mfp-content .mfp-iframe-scaler {
      height: 100%;
      max-height: 100% !important;

      iframe {
        height: 100%;
        max-height: 100% !important;
      }
    }
  }
}


